          IDVariant v_RITENUTAINAI = new IDVariant(0,IDVariant.DECIMAL);
          SQL = new StringBuffer();
          SQL.append("select ");
          SQL.append("  A.ALIQUOTA_INAIL as T87ALIQUINAI, ");
          SQL.append("  A.ALIQUOTA_INAIL_CP as T87ALIQINACP, ");
          SQL.append("  A.ALIQUOTA_INAIL_CE as T87ALIQINACE, ");
          SQL.append("  A.PERC_IMPONIBILE as T87PERCIMPON ");
          SQL.append("from ");
          SQL.append("  T87 A ");
          SQL.append("where (A.CODICE = " + IDL.CSql(IMDB.Value(IMDBDef9.PQRY_RIT2, IMDBDef9.PQSL_RIT2_CODICE_INAIL, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
