  // **********************************************************************
  private void PAN_ECONOMICA_BeforeInsert (IDVariant Cancel)
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;

    try
    {
      TransCount = 0;
      // 
      // Economica Before Insert Event Body
      // Procedure Body
      // 
      if (IMDB.Value(IMDBDef2.TBL_FILTRO5, IMDBDef2.FLD_FILTRO5_ROWNAMESEIMP, 0).equals((new IDVariant(1)), true))
      {
        IMDB.set_Value(IMDBDef9.PQRY_IMPECO11, IMDBDef9.PQSL_IMPECO11_ESERCIZIO, 0, IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSIOESERCI, 0));
      }
      else
      {
        if (PAN_FATTURA.IsNewRow())
        {
          IMDB.set_Value(IMDBDef9.PQRY_IMPECO11, IMDBDef9.PQSL_IMPECO11_ESERCIZIO, 0, IDL.NullValue(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_ANNO_PROT, 0),IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSIOESERCI, 0)));
        }
        else
        {
          IDVariant v_CONTATORE = new IDVariant(0,IDVariant.INTEGER);
          IDVariant v_MAXESERCIZIO = new IDVariant(0,IDVariant.INTEGER);
          SQL = new StringBuffer();
          SQL.append("select ");
          SQL.append("  COUNT(*) as COUNT1, ");
          SQL.append("  MAX(A.ESERCIZIO) as MAXIMPECOESE ");
          SQL.append("from ");
          SQL.append("  IMP_ECO A ");
          SQL.append("where (A.ANNO_PROG = " + IDL.CSql(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_ANNO_PROG, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
          SQL.append("and   (A.NUMERO_PROG = " + IDL.CSql(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_NUMERO_PROG, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
          QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
          if (!QV.EOF()) QV.MoveNext();
          if (!QV.EOF())
          {
            v_CONTATORE = QV.Get("COUNT1", IDVariant.INTEGER) ;
            v_MAXESERCIZIO = QV.Get("MAXIMPECOESE", IDVariant.INTEGER) ;
          }
          QV.Close();
          if (v_CONTATORE.compareTo((new IDVariant(0)), true)>0)
          {
            IMDB.set_Value(IMDBDef9.PQRY_IMPECO11, IMDBDef9.PQSL_IMPECO11_ESERCIZIO, 0, new IDVariant(v_MAXESERCIZIO));
          }
          else
          {
            IMDB.set_Value(IMDBDef9.PQRY_IMPECO11, IMDBDef9.PQSL_IMPECO11_ESERCIZIO, 0, IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSIOESERCI, 0));
          }
        }
      }
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Fattura", "EconomicaBeforeInsertEvent", _e);
    }
  }

