  // **********************************************************************
  // Cfaid: Select into variables
  // Quali dati devi selezionare con questa query?
  // **********************************************************************
  private void ENDMODALEVEN_CFASELINTV88() throws SQLException
  {
    IDVariant[] AggrBuff;
    int[] AggrRowCount;
    int AggrCount=0;
    boolean AggrNewGroup = false;

    IMDB.TblTruncate(IMDBDef9.QRY_CFASELINTV88);
    IMDB.TblTruncate(IMDBDef1.TMP_RECORDSET);
    IMDB.TblMoveFirst(IMDBDef2.TBL_FATTBUONIMDB, 0);
    Loop1: while (!IMDB.Eof(IMDBDef2.TBL_FATTBUONIMDB, 0))
    {
      IMDB.TblAddNew(IMDBDef1.TMP_RECORDSET, 0);
      IMDB.set_Value(IMDBDef1.TMP_RECORDSET, 0, 0, new IDVariant(1));
      IMDB.TblMoveNext(IMDBDef2.TBL_FATTBUONIMDB, 0);
      if (IMDB.Eof(IMDBDef2.TBL_FATTBUONIMDB, 0))
      {
        IMDB.TblMoveFirst(IMDBDef2.TBL_FATTBUONIMDB, 0);
      }
      else
      {
        continue Loop1;
      }
      break;
    }
    IMDB.TblMoveFirst(IMDBDef1.TMP_RECORDSET, 0);
    AggrBuff = new IDVariant[1];
    for (int i=0; i<AggrBuff.length; i++) AggrBuff[i] = new IDVariant();
    AggrRowCount = new int[1];
    AggrCount = 0;
    IMDB.TblMoveFirst(IMDBDef1.TMP_RECORDSET, 0);
    if (IMDB.TblNumRows(IMDBDef1.TMP_RECORDSET) > 0)
    {
      while (true)
      {
        if (AggrCount == 0 && !IMDB.Eof(IMDBDef1.TMP_RECORDSET, 0))
        {
          AggrBuff[0] = new IDVariant(1);
          if (!IDL.IsNull(IMDB.Value(IMDBDef1.TMP_RECORDSET, 0, 0))) AggrRowCount[0] = 1; else AggrRowCount[0] = 0;
          AggrCount = 1;
          IMDB.TblMoveNext(IMDBDef1.TMP_RECORDSET, 0);
        }
        else
        {
          AggrNewGroup = IMDB.Eof(IMDBDef1.TMP_RECORDSET, 0);
          if (AggrNewGroup)
          {
            if (AggrCount > 0)
            {
              IMDB.TblAddNew(IMDBDef9.QRY_CFASELINTV88, 0);
              IMDB.set_Value(IMDBDef9.QRY_CFASELINTV88, 0, 0, new IDVariant(AggrBuff[0]));
            }
            AggrCount = 0;
            if (IMDB.Eof(IMDBDef1.TMP_RECORDSET, 0)) break;
          }
          else
          {
            if (!IMDB.Eof(IMDBDef1.TMP_RECORDSET, 0))
            {
              if (!IDL.IsNull(IMDB.Value(IMDBDef1.TMP_RECORDSET, 0, 0)))
              {
                AggrBuff[0] = IDL.Add(AggrBuff[0], new IDVariant(1));
                AggrRowCount[0]++;
              }
              AggrCount++;
              IMDB.TblMoveNext(IMDBDef1.TMP_RECORDSET, 0);
            }
          }
        }
      }
    }
    IMDB.TblMoveFirst(IMDBDef9.QRY_CFASELINTV88, 0);
  }

  // **********************************************************************
  // Unload Event
  // Evento notificato dal form prima della chiusura dello
  // stesso
  // Cancel: Se impostato a True la chiusura del form viene annullata. - Input/Output
  // Confirm: Rappresenta la scelta dell'utente per la chiusura dei form modali. True se l'utente ha confermato la scelta effettuata, False altrimenti. - Input
  // **********************************************************************
  public void IntFormUnload (IDVariant Cancel, IDVariant Confirm)
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;

    try
    {
      TransCount = 0;
      // 
      // Unload Event Body
      // Procedure Body
      // 
      if (ECONOMMODIFI.booleanValue() && !(IDL.IsNull(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_NUMERO_PROG, 0))))
      {
        IDVariant v_AVVISO = new IDVariant(0,IDVariant.STRING);
        v_AVVISO = (new IDVariant("La videata contiene dati non salvati. Prima di chiudere, salvare i dati o annullare le modifiche."));
        MainFrm.set_AlertMessage(v_AVVISO); 
        Cancel.set((new IDVariant(-1)));
        return;
      }
      if (IDL.IsNull(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_ID_GDM_FE, 0)) && SOSFATCARTAC.equals((new IDVariant("SI")), true))
      {
        if (BLOBALLEGATO.compareTo((new IDVariant("SI")), true)!=0)
        {
          if (FATTURCARTAC.booleanValue())
          {
            IDVariant S = new IDVariant(0,IDVariant.STRING);
            S = (new IDVariant("E' necessario inserire l'allegato"));
            MainFrm.set_AlertMessage(S); 
            Cancel.set((new IDVariant(-1)));
            return;
          }
        }
      }
      if (new IDVariant(PAN_FATTURA.Status()).compareTo((new IDVariant(3)), true)!=0 && new IDVariant(PAN_IVA.Status()).compareTo((new IDVariant(3)), true)!=0 && new IDVariant(PAN_RITENUTE.Status()).compareTo((new IDVariant(3)), true)!=0)
      {
        UNLOADEVENT_FATBUOIMDDEL();
        UNLOADEVENT_IVADELETROWS();
        UNLOADEVENT_IMPECODELETE();
        UNLOADEVENT_FILTRODELETE();
      }
      if ((MainFrm.GetForm(MyGlb.FRM_FATTURE, 0, false)!=null))
      {
        ((Fatture)MainFrm.GetForm(MyGlb.FRM_FATTURE,0)).PAN_FATTURE.PanelCommand(Glb.PCM_REQUERY);
      }
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Fattura", "UnloadEvent", _e);
    }
  }

