  // **********************************************************************
  // Riempi IMDB IVA
  // riempire:  - Input
  // **********************************************************************
  public int RiempiIMDBIVA (IDVariant riempire)
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;
    IDCachedRowSet C3;

    try
    {
      TransCount = 0;
      // 
      // Riempi IMDB IVA Body
      // Procedure Body
      // 
      if (IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_NUMERO_PROG, 0).compareTo(IMDB.Value(IMDBDef9.PQRY_IVA5, IMDBDef9.PQSL_IVA5_NUMERO_PROG, 0), true)!=0 || riempire.booleanValue())
      {
        IDVariant v_RIGA = new IDVariant(0,IDVariant.INTEGER);
        v_RIGA = (new IDVariant(1, IDVariant.INTEGER));
        RIEMPIMDBIVA_IVADELETROWS();
        SQL = new StringBuffer();
        SQL.append("select ");
        SQL.append("  A.PROGRESSIVO as IVAPROGRESSI, ");
        SQL.append("  A.ANNO_PROG as IVAANNOPROG, ");
        SQL.append("  A.NUMERO_PROG as IVANUMERPROG, ");
        SQL.append("  A.CODICE_DOC as IVACODICEDOC, ");
        SQL.append("  A.IMPONIBILE as IVAIMPONIBIL, ");
        SQL.append("  A.IVA as IVA, ");
        SQL.append("  A.ALIQUOTA as IVAALIQUOTA, ");
        SQL.append("  A.IVA_INDED as IVAINDED, ");
        SQL.append("  A.CODICE_ESENZIONE as IVACODICESEN, ");
        SQL.append("  A.CODICE_STAT as IVACODICSTAT, ");
        SQL.append("  A.UTENTE_INSERIMENTO as IVAUTENTINSE, ");
        SQL.append("  A.DATA_INSERIMENTO as IVADATAINSER, ");
        SQL.append("  A.UTENTE_ULTIMO_AGG as IVAUTEULTAGG, ");
        SQL.append("  A.DATA_ULTIMO_AGG as IVADATULTAGG, ");
        SQL.append("  A.TIPO_REGISTRO_S as IVATIPOREGI1, ");
        SQL.append("  A.TIPO_BOLLATO_S as IVATIPOBOLLS, ");
        SQL.append("  A.ANNO_REG_S as IVAANNOREGS, ");
        SQL.append("  A.NUMERO_REG_S as IVANUMERREGS, ");
        SQL.append("  A.D_DATA_REG_S as IVADDATAREGS, ");
        SQL.append("  A.TIPO_REGISTRO as IVATIPOREGIS, ");
        SQL.append("  A.SERVIZIO as IVASERVIZIO, ");
        SQL.append("  A.ANNO_REG as IVAANNOREG, ");
        SQL.append("  A.NUMERO_REG as IVANUMEROREG, ");
        SQL.append("  A.D_DATA_REG as IVADDATAREG, ");
        SQL.append("  A.D_DATA_LIQ as IVADDATALIQ, ");
        SQL.append("  A.TIPO_DOC as IVATIPODOC, ");
        SQL.append("  A.REC_VENDITE as IVARECVENDIT ");
        SQL.append("from ");
        SQL.append("  IVA A ");
        SQL.append("where (A.ANNO_PROG = " + IDL.CSql(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_ANNO_PROG, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
        SQL.append("and   (A.NUMERO_PROG = " + IDL.CSql(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_NUMERO_PROG, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
        SQL.append("order by ");
        SQL.append("  A.PROGRESSIVO ");
        C3 = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
        if (!C3.EOF()) C3.MoveNext();
        while (!C3.EOF())
        {
          RIEMPIMDBIVA_IVAINSERVALU(C3.Get("IVAPROGRESSI"), C3.Get("IVAANNOPROG"), C3.Get("IVANUMERPROG"), C3.Get("IVACODICEDOC"), C3.Get("IVAIMPONIBIL"), C3.Get("IVARECVENDIT"), C3.Get("IVA"), C3.Get("IVAALIQUOTA"), C3.Get("IVAINDED"), C3.Get("IVACODICESEN"), C3.Get("IVACODICSTAT"), C3.Get("IVAUTENTINSE"), C3.Get("IVADATAINSER"), C3.Get("IVAUTEULTAGG"), C3.Get("IVADATULTAGG"), C3.Get("IVATIPOREGI1"), C3.Get("IVATIPOBOLLS"), C3.Get("IVAANNOREGS"), C3.Get("IVANUMERREGS"), C3.Get("IVADDATAREGS"), C3.Get("IVATIPOREGIS"), C3.Get("IVASERVIZIO"), C3.Get("IVAANNOREG"), C3.Get("IVANUMEROREG"), C3.Get("IVADDATAREG"), C3.Get("IVADDATALIQ"), C3.Get("IVATIPODOC"), v_RIGA);
          v_RIGA = IDL.Add(v_RIGA, (new IDVariant(1)));
          C3.MoveNext();
        }
        C3.Close();
      }
      return 0;
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Fattura", "RiempiIMDBIVA", _e);
      return -1;
    }
  }

