  // **********************************************************************
  // Scadenza
  // **********************************************************************
  public IDVariant Scadenza ()
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;
    IDVariant v_NUM = new IDVariant(0,IDVariant.INTEGER);
    IDVariant v_DEC1 = new IDVariant(0,IDVariant.STRING);
    IDVariant v_DEC2 = new IDVariant(0,IDVariant.INTEGER);
    IDVariant v_DDATA = new IDVariant(0,IDVariant.DATETIME);
    IDVariant v_MESE = new IDVariant(0,IDVariant.INTEGER);
    IDVariant v_GIORNO = new IDVariant(0,IDVariant.INTEGER);
    IDVariant v_ANNO = new IDVariant(0,IDVariant.INTEGER);

    try
    {
      TransCount = 0;
      // 
      // Scadenza Body
      // Procedure Body
      // 
      v_DDATA = (new IDVariant());
      SQL = new StringBuffer();
      SQL.append("select ");
      SQL.append("  A.NUM_GIORNI as T01NUMGIORNI, ");
      SQL.append("  A.DECORRENZA1 as T01DECORREN1, ");
      SQL.append("  A.DECORRENZA2 as T01DECORRENZ ");
      SQL.append("from ");
      SQL.append("  T01 A ");
      SQL.append("where (A.CODICE = " + IDL.CSql(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_TIPO_PAGAMENTO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
      if (!QV.EOF()) QV.MoveNext();
      if (!QV.EOF())
      {
        v_NUM = QV.Get("T01NUMGIORNI", IDVariant.INTEGER) ;
        v_DEC1 = QV.Get("T01DECORREN1", IDVariant.STRING) ;
        v_DEC2 = QV.Get("T01DECORRENZ", IDVariant.INTEGER) ;
      }
      QV.Close();
      if (IDL.IsNull(v_NUM))
      {
        v_DDATA = (new IDVariant());
      }
      else
      {
        if (v_DEC1.equals((new IDVariant("E")), true) || v_DEC1.equals((new IDVariant("D")), true))
        {
          v_DDATA = IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_D_DATA_DOC, 0);
        }
        else if (v_DEC1.equals((new IDVariant("P")), true))
        {
          v_DDATA = IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_D_DATA_PROT, 0);
        }
        else if (v_DEC1.equals((new IDVariant("A")), true))
        {
          v_DDATA = IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_DATA_ARRIVO, 0);
        }
        else
        {
          v_DDATA = (new IDVariant());
        }
      }
      if (!(IDL.IsNull(v_DDATA)))
      {
        v_DDATA = IDL.DateAdd((new IDVariant("d")),v_NUM,v_DDATA);
        v_MESE = IDL.Month(v_DDATA);
        v_GIORNO = IDL.Day(v_DDATA);
        v_ANNO = IDL.Year(v_DDATA);
        if (v_DEC2.equals((new IDVariant(2)), true))
        {
          if (v_MESE.equals((new IDVariant(4)), true) || v_MESE.equals((new IDVariant(6)), true) || v_MESE.equals((new IDVariant(9)), true) || v_MESE.equals((new IDVariant(11)), true))
          {
            v_GIORNO = (new IDVariant(30));
          }
          else
          {
            if (v_MESE.equals((new IDVariant(2)), true))
            {
              v_GIORNO = (new IDVariant(28));
            }
            else
            {
              v_GIORNO = (new IDVariant(31));
            }
          }
          v_DDATA = IDL.ToDate(v_ANNO, v_MESE, v_GIORNO);
        }
        else
        {
          if (v_DEC2.equals((new IDVariant(3)), true))
          {
            if (v_GIORNO.compareTo((new IDVariant(15)), true)>0)
            {
              if (v_MESE.equals((new IDVariant(4)), true) || v_MESE.equals((new IDVariant(6)), true) || v_MESE.equals((new IDVariant(9)), true) || v_MESE.equals((new IDVariant(11)), true))
              {
                v_GIORNO = (new IDVariant(30));
              }
              else
              {
                if (v_MESE.equals((new IDVariant(2)), true))
                {
                  v_GIORNO = (new IDVariant(28));
                }
                else
                {
                  v_GIORNO = (new IDVariant(31));
                }
              }
              v_DDATA = IDL.ToDate(v_ANNO, v_MESE, v_GIORNO);
            }
            else
            {
              v_DDATA = IDL.ToDate(v_ANNO, v_MESE, (new IDVariant(15)));
            }
          }
        }
      }
      return v_DDATA;
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Fattura", "Scadenza", _e);
      return new IDVariant();
    }
  }

  // **********************************************************************
  // Salva Economica
  // **********************************************************************
  private int SalvaEconomica ()
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;

    try
    {
      TransCount = 0;
      // 
      // Salva Economica Body
      // Procedure Body
      // 
      if (ECONOMMODIFI.booleanValue() || new IDVariant(PAN_FATTURA.Status()).equals((new IDVariant(3)), true) || new IDVariant(PAN_IVA.Status()).equals((new IDVariant(3)), true))
      {
        IDVariant v_SUM = new IDVariant(0,IDVariant.DECIMAL);
        v_SUM = PAN_ECONOMICA.GetFieldSum(PFL_ECONOMICA_IMPORTO);
        if (v_SUM.compareTo(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_IMPORTO, 0), true)!=0)
        {
          IDVariant v_SMS = new IDVariant(0,IDVariant.STRING);
          v_SMS = (new IDVariant("Totale Imputazioni Economiche diverso dall'importo del Documento.", IDVariant.STRING));
          MainFrm.set_AlertMessage(v_SMS); 
          return 0;
        }
        if (new IDVariant(PAN_FATTURA.Status()).equals((new IDVariant(3)), true) || new IDVariant(PAN_IVA.Status()).equals((new IDVariant(3)), true))
        {
          SalvaFattura();
        }
        else
        {
          MainFrm.Cf4armDBObject.BeginTrans();
          if (SalvaIMPECO())
          {
            MainFrm.Cf4armDBObject.CommitTrans();
          }
          else
          {
            MainFrm.Cf4armDBObject.RollbackTrans();
          }
        }
      }
      return 0;
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Fattura", "SalvaEconomica", _e);
      return -1;
    }
  }

  // **********************************************************************
  // Controlli Nn Bloccanti
  // **********************************************************************
  public boolean ControlliNnBloccanti ()
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;
    IDVariant v_SALVATO = new IDVariant(0,IDVariant.INTEGER);
    IDVariant v_MESSAGGIO = new IDVariant(0,IDVariant.STRING);
    IDVariant v_ERRORE = new IDVariant(0,IDVariant.INTEGER);

    try
    {
      TransCount = 0;
      // 
      // Controlli Nn Bloccanti Body
      // Procedure Body
      // 
      v_SALVATO = (new IDVariant(0));
      v_ERRORE = (new IDVariant(0));
      if (IMDB.Value(IMDBDef7.TBL_PARAMETIRIDE, IMDBDef7.FLD_PARAMETIRIDE_DOCUMENIRIDE, 0).equals((new IDVariant("SI")), true))
      {
        if (PAN_FATTURA.IsNewRow() && IDL.IsNull(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_UFFICIO_INVIO, 0)))
        {
          IDVariant v_SMS = new IDVariant(0,IDVariant.STRING);
          v_SMS = (new IDVariant("Ufficio non indicato. La fattura non sar inserita nel documentale."));
          v_MESSAGGIO = IDL.Add(IDL.Add(v_MESSAGGIO, v_SMS), (new IDVariant("<BR/>")));
          v_ERRORE = (new IDVariant(-1));
        }
      }
      if (IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_D_DATA_DOC, 0).compareTo(IDL.Today(), true)<0)
      {
        // 
        // Per fatture Cliente e nota di accredito cliente
        // 
        if (IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_CODICE_DOC, 0).equals((new IDVariant(3)), true) || IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_CODICE_DOC, 0).equals((new IDVariant(4)), true))
        {
          IDVariant v_SMS = new IDVariant(0,IDVariant.STRING);
          v_SMS = (new IDVariant("Attenzione: Data documento precedente alla data odierna!", IDVariant.STRING));
          v_MESSAGGIO = IDL.Add(IDL.Add(v_MESSAGGIO, v_SMS), (new IDVariant("<BR/>")));
          v_ERRORE = (new IDVariant(-1));
        }
      }
      // 
      // controllo quadratura iva
      // 
      {
        if ((new IDVariant(PAN_IVA.GetNumRows())).compareTo((new IDVariant(0)), true)>0 || new IDVariant(PAN_IVA.Status()).equals((new IDVariant(3)), true))
        {
          IDVariant v_TOTALE = new IDVariant(0,IDVariant.DECIMAL);
          v_TOTALE = IDL.Add(IDL.ToCurrency((new IDVariant(PAN_IVA.FieldText(PFL_IVA_TOTALEIMPONI)))), IDL.ToCurrency((new IDVariant(PAN_IVA.FieldText(PFL_IVA_TOTALEIVA)))));
          if (IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_IMPORTO, 0).compareTo(v_TOTALE, true)!=0)
          {
            IDVariant v_SMS = new IDVariant(0,IDVariant.STRING);
            v_SMS = (new IDVariant("Quadratura IVA errata.", IDVariant.STRING));
            v_MESSAGGIO = IDL.Add(IDL.Add(v_MESSAGGIO, v_SMS), (new IDVariant("<BR/>")));
            v_ERRORE = (new IDVariant(-1));
          }
        }
      }
      // 
      // controlli imputazione economica
      // 
      {
        if (ECONOMMODIFI.booleanValue())
        {
          IDVariant v_TIPOFATTORE = new IDVariant(0,IDVariant.STRING);
          SQL = new StringBuffer();
          SQL.append("select ");
          SQL.append("  A.TIPO as FATTORITIPO ");
          SQL.append("from ");
          SQL.append("  FATTORI A ");
          SQL.append("where (A.FATTORE = " + IDL.CSql(IMDB.Value(IMDBDef9.PQRY_IMPECO11, IMDBDef9.PQSL_IMPECO11_FATTORE, 0), IDL.FMT_CHAR, MainFrm.Cf4armDBObject.DBO()) + ") ");
          QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
          if (!QV.EOF()) QV.MoveNext();
          if (!QV.EOF())
          {
            v_TIPOFATTORE = QV.Get("FATTORITIPO", IDVariant.STRING) ;
          }
          QV.Close();
          if (v_TIPOFATTORE.equals((new IDVariant("P")), true) && IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSIOESERCI, 0).compareTo(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_ANNO_PROT, 0), true)!=0)
          {
            IDVariant v_SMS = new IDVariant(0,IDVariant.STRING);
            v_SMS = (new IDVariant("Attenzione. L'imputazione su Conto Patrimoniale ", IDVariant.STRING));
            IDVariant v_SMS1 = new IDVariant(0,IDVariant.STRING);
            v_SMS1 = (new IDVariant(" verr registrata sul Conto del Patrimonio dell'esercizio ", IDVariant.STRING));
            v_MESSAGGIO = IDL.Add(IDL.Add(IDL.Add(IDL.Add(IDL.Add(v_MESSAGGIO, v_SMS), IMDB.Value(IMDBDef9.PQRY_IMPECO11, IMDBDef9.PQSL_IMPECO11_FATTORE, 0)), v_SMS1), IDL.ToString(IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSIOESERCI, 0))), (new IDVariant("<BR/>")));
            v_ERRORE = (new IDVariant(-1));
          }
        }
      }
      if (v_ERRORE.booleanValue() || !(IDL.IsNull(MESSANONBLOC)))
      {
        IDVariant v_SMS = new IDVariant(0,IDVariant.STRING);
        v_SMS = (new IDVariant("Si desidera continuare?", IDVariant.STRING));
        IDVariant v_CONFERMA = new IDVariant(0,IDVariant.INTEGER);
        v_MESSAGGIO = IDL.Add(IDL.Add(IDL.Add(v_MESSAGGIO, MESSANONBLOC), (new IDVariant("<BR/>"))), v_SMS);
        v_CONFERMA = (new IDVariant(MainFrm.MessageConfirm(v_MESSAGGIO)));
        MESSANONBLOC = (new IDVariant());
        if (!(v_CONFERMA.booleanValue()))
        {
          v_SALVATO = (new IDVariant(0));
          return v_SALVATO.booleanValue();
        }
      }
      v_SALVATO = (new IDVariant(-1));
      return v_SALVATO.booleanValue();
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Fattura", "ControlliNnBloccanti", _e);
      return false;
    }
  }

