  // **********************************************************************
  // Cancella Economica
  // **********************************************************************
  public int CancellaEconomica ()
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;

    try
    {
      TransCount = 0;
      // 
      // Cancella Economica Body
      // Procedure Body
      // 
      if (ConfermaCancellazione())
      {
        if (IDL.NullValue(MainFrm.GESTIOECONOM,(new IDVariant(-1))).compareTo((new IDVariant(-1)), true)!=0 && MODALIIMPUTA.equals((new IDVariant("FA")), true))
        {
          IDVariant v_SMS1 = new IDVariant(0,IDVariant.STRING);
          v_SMS1 = (new IDVariant("Non  possibile cancellare imputazioni economiche", IDVariant.STRING));
          // 
          // 
          // 
          // 
          if ((new IDVariant(PAN_ECONOMICA.GetNumRows())).equals((new IDVariant(1)), true))
          {
            MainFrm.set_AlertMessage(v_SMS1); 
            return 0;
          }
          else
          {
            // 
            // controllo importo imputazione
            // 
            {
              IDVariant v_SOMMA = new IDVariant(0,IDVariant.DECIMAL);
              v_SOMMA = PAN_ECONOMICA.GetFieldSum(PFL_ECONOMICA_IMPORTO);
              if (IDL.Sub(v_SOMMA, IMDB.Value(IMDBDef9.PQRY_IMPECO11, IMDBDef9.PQSL_IMPECO11_IMPORTO, 0)).compareTo(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_IMPORTO, 0), true)!=0)
              {
                IDVariant v_SMS = new IDVariant(0,IDVariant.STRING);
                v_SMS = (new IDVariant("Totale Imputazioni Economiche diverso dall'importo del Documento.", IDVariant.STRING));
                MainFrm.set_AlertMessage(v_SMS); 
                return 0;
              }
            }
          }
        }
        MainFrm.Cf4armDBObject.BeginTrans();
        try
        {
          SQL = new StringBuffer();
          SQL.append("delete from IMP_ECO ");
          SQL.append("where (ESERCIZIO = " + IDL.CSql(IMDB.Value(IMDBDef9.PQRY_IMPECO11, IMDBDef9.PQSL_IMPECO11_ESERCIZIO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
          SQL.append("and   (PROGRESSIVO = " + IDL.CSql(IMDB.Value(IMDBDef9.PQRY_IMPECO11, IMDBDef9.PQSL_IMPECO11_PROGRESSIVO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
          MainFrm.Cf4armDBObject.DBO().Execute(SQL);
          if (ECONOMMODIFI.booleanValue())
          {
            if (SalvaIMPECO())
            {
              MainFrm.Cf4armDBObject.CommitTrans();
              RiempiIMDBIMPECO((new IDVariant(-1)));
            }
            else
            {
              MainFrm.Cf4armDBObject.RollbackTrans();
            }
            return 0;
          }
          PAN_ECONOMICA.PanelCommand(Glb.PCM_DELETE);
        }
        catch (Exception e15)
        {
          MainFrm.Cf4armDBObject.RollbackTrans();
          MainFrm.set_AlertMessage(new IDVariant(e15.getMessage())); 
          return 0;
        }
        MainFrm.Cf4armDBObject.CommitTrans();
      }
      return 0;
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Fattura", "CancellaEconomica", _e);
      return -1;
    }
  }

