  // **********************************************************************
  // Controlli Iva PS RC
  // Tipo Registro:  - Input
  // Tipo Bollato:  - Input
  // Tipo Fattura:  - Input
  // **********************************************************************
  public IDVariant ControlliIvaPSRC (IDVariant TipoRegistro, IDVariant TipoBollato, IDVariant TipoFattura)
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;

    try
    {
      TransCount = 0;
      // 
      // Controlli Iva PS RC Body
      // Corpo Procedura
      // 
      IDVariant v_MESSANONBLOC = null;
      v_MESSANONBLOC = (new IDVariant());
      IDVariant v_AVVISOVENDIT = new IDVariant(0,IDVariant.STRING);
      v_AVVISOVENDIT = (new IDVariant("Registro Vendite per "));
      IDVariant v_AVVISOIMPEGN = new IDVariant(0,IDVariant.STRING);
      v_AVVISOIMPEGN = (new IDVariant("Impegno per "));
      IDVariant v_AVVISOACCERT = new IDVariant(0,IDVariant.STRING);
      v_AVVISOACCERT = (new IDVariant("Accertamento per "));
      IDVariant v_NONINDICATO = new IDVariant(0,IDVariant.STRING);
      v_NONINDICATO = (new IDVariant(" non indicato per il Registro Definitivo"));
      IDVariant v_TIPOBOLVENSP = null;
      v_TIPOBOLVENSP = (new IDVariant());
      IDVariant v_TIPOBOLVENRC = null;
      v_TIPOBOLVENRC = (new IDVariant());
      IDVariant v_NUMEROIMPSP = null;
      v_NUMEROIMPSP = (new IDVariant());
      IDVariant v_NUMEROIMPRC = null;
      v_NUMEROIMPRC = (new IDVariant());
      IDVariant v_NUMEROACCSP = null;
      v_NUMEROACCSP = (new IDVariant());
      IDVariant v_ISTITUZIONAL = null;
      v_ISTITUZIONAL = (new IDVariant());
      SQL = new StringBuffer();
      SQL.append("select ");
      SQL.append("  A.TIPO_BOLLATO_VENDITE_SP as T04TIPBOVESP, ");
      SQL.append("  A.TIPO_BOLLATO_VENDITE_RC as T04TIPBOVERC, ");
      SQL.append("  A.NUMERO_IMP_SP as T04NUMEIMPSP, ");
      SQL.append("  A.NUMERO_IMP_RC as T04NUMEIMPRC, ");
      SQL.append("  A.NUMERO_ACC_SP as T04NUMEACCSP, ");
      SQL.append("  B.ISTITUZIONALE as T09ISTITUZIO ");
      SQL.append("from ");
      SQL.append("  T04 A, ");
      SQL.append("  T09 B ");
      SQL.append("where (B.CODICE(+) = A.ATTIVITA) ");
      SQL.append("and   (A.TIPO_REGISTRO = " + IDL.CSql(TipoRegistro, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.TIPO_BOLLATO = " + IDL.CSql(TipoBollato, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
      if (!QV.EOF()) QV.MoveNext();
      if (!QV.EOF())
      {
        v_TIPOBOLVENSP = QV.Get("T04TIPBOVESP", IDVariant.INTEGER) ;
        v_TIPOBOLVENRC = QV.Get("T04TIPBOVERC", IDVariant.INTEGER) ;
        v_NUMEROIMPSP = QV.Get("T04NUMEIMPSP", IDVariant.INTEGER) ;
        v_NUMEROIMPRC = QV.Get("T04NUMEIMPRC", IDVariant.INTEGER) ;
        v_NUMEROACCSP = QV.Get("T04NUMEACCSP", IDVariant.INTEGER) ;
        v_ISTITUZIONAL = QV.Get("T09ISTITUZIO", IDVariant.STRING) ;
      }
      QV.Close();
      if (IDL.NullValue(v_ISTITUZIONAL,(new IDVariant("NO"))).equals((new IDVariant("NO")), true))
      {
        if (TipoFattura.equals((new IDVariant("SP")), true) && IDL.IsNull(v_TIPOBOLVENSP))
        {
          v_MESSANONBLOC = IDL.Add(IDL.Add(v_AVVISOVENDIT, ((new IDVariant("SP")).equals((new IDVariant("SP")))? new IDVariant("Split Payment") : (new IDVariant("SP")).equals((new IDVariant("RC")))? new IDVariant("Reverse Charge") : (new IDVariant("SP")).equals((new IDVariant()))? new IDVariant("Standard") :  new IDVariant())), v_NONINDICATO);
        }
        if (TipoFattura.equals((new IDVariant("RC")), true) && IDL.IsNull(v_TIPOBOLVENRC))
        {
          v_MESSANONBLOC = IDL.Add(IDL.Add(v_AVVISOVENDIT, ((new IDVariant("RC")).equals((new IDVariant("SP")))? new IDVariant("Split Payment") : (new IDVariant("RC")).equals((new IDVariant("RC")))? new IDVariant("Reverse Charge") : (new IDVariant("RC")).equals((new IDVariant()))? new IDVariant("Standard") :  new IDVariant())), v_NONINDICATO);
        }
      }
      else
      {
        if (TipoFattura.equals((new IDVariant("SP")), true) && IDL.IsNull(v_NUMEROIMPSP))
        {
          v_MESSANONBLOC = IDL.Add(IDL.Add(v_AVVISOIMPEGN, ((new IDVariant("SP")).equals((new IDVariant("SP")))? new IDVariant("Split Payment") : (new IDVariant("SP")).equals((new IDVariant("RC")))? new IDVariant("Reverse Charge") : (new IDVariant("SP")).equals((new IDVariant()))? new IDVariant("Standard") :  new IDVariant())), v_NONINDICATO);
        }
        if (TipoFattura.equals((new IDVariant("RC")), true) && IDL.IsNull(v_NUMEROIMPRC))
        {
          v_MESSANONBLOC = IDL.Add(IDL.Add(v_AVVISOIMPEGN, ((new IDVariant("RC")).equals((new IDVariant("SP")))? new IDVariant("Split Payment") : (new IDVariant("RC")).equals((new IDVariant("RC")))? new IDVariant("Reverse Charge") : (new IDVariant("RC")).equals((new IDVariant()))? new IDVariant("Standard") :  new IDVariant())), v_NONINDICATO);
        }
      }
      if (TipoFattura.equals((new IDVariant("SP")), true) && IDL.IsNull(v_NUMEROACCSP))
      {
        v_MESSANONBLOC = IDL.Add(IDL.Add(IDL.Add(IDL.Add(v_MESSANONBLOC, (new IDVariant("<BR/>"))), v_AVVISOACCERT), ((new IDVariant("SP")).equals((new IDVariant("SP")))? new IDVariant("Split Payment") : (new IDVariant("SP")).equals((new IDVariant("RC")))? new IDVariant("Reverse Charge") : (new IDVariant("SP")).equals((new IDVariant()))? new IDVariant("Standard") :  new IDVariant())), v_NONINDICATO);
      }
      return v_MESSANONBLOC;
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Fattura", "ControlliIvaPSRC", _e);
      return new IDVariant();
    }
  }

