  public int SegnalazioneBollatoDefinitivo ()
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;

    try
    {
      TransCount = 0;
      // 
      // Segnalazione Bollato Definitivo Body
      // Corpo Procedura
      // 
      if (!(IDL.IsNull(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_TRATTAMENTO_IVA, 0))) && (IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_CODICE_DOC, 0).equals((new IDVariant(1)), true) || IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_CODICE_DOC, 0).equals((new IDVariant(2)), true) || IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_CODICE_DOC, 0).equals((new IDVariant(7)), true)))
      {
        SEGNBOLLDEFI_IVA2();
        while (!IMDB.Eof(IMDBDef10.QRY_IVA2, 0))
        {
          if (!(IDL.IsNull(IMDB.Value(IMDBDef10.QRY_IVA2, IMDBDef10.QSL_IVA2_TIPOREGISTRO, 0))) && !(IDL.IsNull(IMDB.Value(IMDBDef10.QRY_IVA2, IMDBDef10.QSL_IVA2_SERVIZIO, 0))) && IDL.IsNull(IMDB.Value(IMDBDef10.QRY_IVA2, IMDBDef10.QSL_IVA2_TIPOREGISTRS, 0)) && IDL.IsNull(IMDB.Value(IMDBDef10.QRY_IVA2, IMDBDef10.QSL_IVA2_TIPOBOLLATOS, 0)))
          {
            IDVariant v_ISTITUZIONAL = new IDVariant(0,IDVariant.STRING);
            IDVariant v_DESCRIZIONE = new IDVariant(0,IDVariant.STRING);
            SQL = new StringBuffer();
            SQL.append("select ");
            SQL.append("  B.ISTITUZIONALE as T09ISTITUZIO, ");
            SQL.append("  A.DESCRIZIONE as T04DESCRIZIO ");
            SQL.append("from ");
            SQL.append("  T04 A, ");
            SQL.append("  T09 B ");
            SQL.append("where (B.CODICE(+) = A.ATTIVITA) ");
            SQL.append("and   (A.TIPO_REGISTRO = " + IDL.CSql(IMDB.Value(IMDBDef10.QRY_IVA2, IMDBDef10.QSL_IVA2_TIPOREGISTRO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
            SQL.append("and   (A.TIPO_BOLLATO = " + IDL.CSql(IMDB.Value(IMDBDef10.QRY_IVA2, IMDBDef10.QSL_IVA2_SERVIZIO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
            QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
            if (!QV.EOF()) QV.MoveNext();
            if (!QV.EOF())
            {
              v_ISTITUZIONAL = QV.Get("T09ISTITUZIO", IDVariant.STRING) ;
              v_DESCRIZIONE = QV.Get("T04DESCRIZIO", IDVariant.STRING) ;
            }
            QV.Close();
            if (IDL.NullValue(v_ISTITUZIONAL,(new IDVariant("NO"))).equals((new IDVariant("NO")), true))
            {
              IDVariant v_AVVISO1 = new IDVariant(0,IDVariant.STRING);
              v_AVVISO1 = (new IDVariant("E' stato indicato il Bollato Definitivo "));
              IDVariant v_AVVISO2 = new IDVariant(0,IDVariant.STRING);
              v_AVVISO2 = (new IDVariant("Per visualizzare l'iva a debito nella liquidazione iva, registrare anche l'IVA Vendite sul documento."));
              MainFrm.set_AlertMessage(IDL.Add(IDL.Add(IDL.Add(IDL.Add(v_AVVISO1, v_DESCRIZIONE), (new IDVariant("."))), (new IDVariant("<BR/>"))), v_AVVISO2)); 
              return 0;
            }
          }
          IMDB.TblMoveNext(IMDBDef10.QRY_IVA2, 0);
        }
      }
      return 0;
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Fattura", "SegnalazioneBollatoDefinitivo", _e);
      return -1;
    }
  }

  // **********************************************************************
  // IVA
  // Quali dati devi selezionare con questa query?
  // **********************************************************************
  private void SEGNBOLLDEFI_IVA2() throws SQLException
  {
    IDVariant[] AggrBuff;
    int[] AggrRowCount;
    int AggrCount=0;
    boolean AggrNewGroup = false;

    IMDB.TblTruncate(IMDBDef10.QRY_IVA2);
    IMDB.TblMoveFirst(IMDBDef2.TBL_IVA4, 0);
    Loop1: while (!IMDB.Eof(IMDBDef2.TBL_IVA4, 0))
    {
      if (IMDB.Value(IMDBDef2.TBL_IVA4, IMDBDef2.FLD_IVA4_NUMERO_PROG, 0).equals(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_NUMERO_PROG, 0), true))
      {
        if (IMDB.Value(IMDBDef2.TBL_IVA4, IMDBDef2.FLD_IVA4_ANNO_PROG, 0).equals(IMDB.Value(IMDBDef9.PQRY_FAT11, IMDBDef9.PQSL_FAT11_ANNO_PROG, 0), true))
        {
          IMDB.TblAddNew(IMDBDef10.QRY_IVA2, 0);
          IMDB.TblLinkRow(IMDBDef10.QRY_IVA2, 0, IMDBDef2.TBL_IVA4, 0);
          IMDB.TblLinkField(IMDBDef10.QRY_IVA2, 0, 0, IMDBDef2.TBL_IVA4, IMDBDef2.FLD_IVA4_TIPO_REGISTRO, 0);
          IMDB.TblLinkField(IMDBDef10.QRY_IVA2, 1, 0, IMDBDef2.TBL_IVA4, IMDBDef2.FLD_IVA4_SERVIZIO, 0);
          IMDB.TblLinkField(IMDBDef10.QRY_IVA2, 2, 0, IMDBDef2.TBL_IVA4, IMDBDef2.FLD_IVA4_TIPO_REGISTRO_S, 0);
          IMDB.TblLinkField(IMDBDef10.QRY_IVA2, 3, 0, IMDBDef2.TBL_IVA4, IMDBDef2.FLD_IVA4_TIPO_BOLLATO_S, 0);
        }
      }
      IMDB.TblMoveNext(IMDBDef2.TBL_IVA4, 0);
      if (IMDB.Eof(IMDBDef2.TBL_IVA4, 0))
      {
        IMDB.TblMoveFirst(IMDBDef2.TBL_IVA4, 0);
      }
      else
      {
        continue Loop1;
      }
      break;
    }
    IMDB.TblMoveFirst(IMDBDef10.QRY_IVA2, 0);
  }

