  // **********************************************************************
  // Impegno On Command Event
  // Evento notificato dal pannello prima di eseguire un
  // comando di pannello.
  // Command: E' un numero intero che rappresenta il comando che sta per essere eseguito. Deve essere confrontato con uno dei valori della lista valori PanelCommands. - Input
  // Cancel: Pu essere impostato a True per cancellare il comando. - Input/Output
  // User Operation - Input
  // **********************************************************************
  private void PAN_IMPEGNO_OnPanelCommand(IDVariant Command,IDVariant Cancel,IDVariant UserOperation)
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;

    try
    {
      TransCount = 0;
      // 
      // Impegno On Command Event Body
      // Procedure Body
      // 
      if (Command.equals((new IDVariant(8)), true))
      {
        VisibilitCampiIntCapArt();
      }
      if (Command.equals((new IDVariant(16)), true))
      {
        MainFrm.Cf4armDBObject.set_ErrorMessage((new IDVariant("")).stringValue());
        MainFrm.Cf4armDBObject.IMPPD(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_ANNO_IMP, 0), IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_NUMERO_IMP, 0));
        if ((new IDVariant(MainFrm.Cf4armDBObject.ErrorMessage())).compareTo((new IDVariant("")), true)!=0)
        {
          MainFrm.set_AlertMessage((new IDVariant(MainFrm.Cf4armDBObject.ErrorMessage()))); 
          Cancel.set((new IDVariant(-1)));
          return;
        }
        if (!(IDL.IsNull(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_PROGR_UNITA_ORGANIZZATIVA, 0))))
        {
          MainFrm.Cf4armDBObject.set_ErrorMessage((new IDVariant("")).stringValue());
          MainFrm.Cf4armDBObject.IMPUODI(IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSIOESERCI, 0), IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_CAPITOLO, 0), IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_ARTICOLO, 0), IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_ANNO_IMP, 0), IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_NUMERO_IMP, 0), IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_CONS_SVILUPPO, 0), IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_PROGR_UNITA_ORGANIZZATIVA, 0), IDL.ToFloat(PAN_IMPEGNO.GetOrgValue(PFL_IMPEGNO_IMPORTO)), IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_IMPORTO, 0), IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_ANNO_INTROD, 0));
          if ((new IDVariant(MainFrm.Cf4armDBObject.ErrorMessage())).compareTo((new IDVariant("")), true)!=0)
          {
            MainFrm.set_AlertMessage((new IDVariant(MainFrm.Cf4armDBObject.ErrorMessage()))); 
            Cancel.set((new IDVariant(-1)));
            return;
          }
        }
        IDVariant v_VCOUNT = new IDVariant(0,IDVariant.INTEGER);
        SQL = new StringBuffer();
        SQL.append("select ");
        SQL.append("  COUNT(*) as COUNT1 ");
        SQL.append("from ");
        SQL.append("  IMP A ");
        SQL.append("where (A.NUMERO_PLUR = " + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_NUMERO_IMP, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
        SQL.append("and   (A.ANNO_PLUR = " + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_ANNO_IMP, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
        QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
        if (!QV.EOF()) QV.MoveNext();
        if (!QV.EOF())
        {
          v_VCOUNT = QV.Get("COUNT1", IDVariant.INTEGER) ;
        }
        QV.Close();
        if (v_VCOUNT.compareTo((new IDVariant(0)), true)>0)
        {
          IDVariant S = new IDVariant(0,IDVariant.STRING);
          S = (new IDVariant("Esistono degli impegni pluriennali. Impossibile cancellare l'impegno"));
          MainFrm.set_AlertMessage(S); 
          Cancel.set((new IDVariant(-1)));
          return;
        }
      }
      if (Command.equals((new IDVariant(32)), true) || Command.equals((new IDVariant(64)), true))
      {
        IMDB.set_Value(IMDBDef4.TBL_PARAMETRI20, IMDBDef4.FLD_PARAMETRI20_ROWNAMCAPOLD, 0, ((PAN_IMPEGNO.GetOrgValue(PFL_IMPEGNO_CAPITOLO2).equals((new IDVariant())))?(new IDVariant()):IDL.ToCurrency(PAN_IMPEGNO.GetOrgValue(PFL_IMPEGNO_CAPITOLO2))));
        IMDB.set_Value(IMDBDef4.TBL_PARAMETRI20, IMDBDef4.FLD_PARAMETRI20_ROWNAMARTOLD, 0, ((PAN_IMPEGNO.GetOrgValue(PFL_IMPEGNO_ARTICOLO2).equals((new IDVariant())))?(new IDVariant()):IDL.ToInteger(PAN_IMPEGNO.GetOrgValue(PFL_IMPEGNO_ARTICOLO2))));
        IMDB.set_Value(IMDBDef4.TBL_OPERA, IMDBDef4.FLD_OPERA_ROWNAMEOPERA, 0, ((PAN_IMPEGNO.GetOrgValue(PFL_IMPEGNO_OPERA).equals((new IDVariant())))?(new IDVariant()):IDL.ToInteger(PAN_IMPEGNO.GetOrgValue(PFL_IMPEGNO_OPERA))));
        IMDB.set_Value(IMDBDef4.TBL_OPERA, IMDBDef4.FLD_OPERA_ROWNAMVOCTEC, 0, ((PAN_IMPEGNO.GetOrgValue(PFL_IMPEGNO_VOCETECNOPER).equals((new IDVariant())))?(new IDVariant()):IDL.ToInteger(PAN_IMPEGNO.GetOrgValue(PFL_IMPEGNO_VOCETECNOPER))));
        if (new IDVariant(PAN_FINANZIAMENT.Status()).equals((new IDVariant(3)), true))
        {
          PAN_FINANZIAMENT.PanelCommand(Glb.PCM_CANCEL);
        }
        if (new IDVariant(PAN_PLURIENNALI.Status()).equals((new IDVariant(3)), true))
        {
          PAN_PLURIENNALI.PanelCommand(Glb.PCM_CANCEL);
        }
        IMPECODISABI = (new IDVariant(0));
        ValorizzaAutoImputazioneEco((new IDVariant("SI")));
      }
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Impegno", "ImpegnoOnCommandEvent", _e);
    }
  }

