  // **********************************************************************
  // Chk Percento 12
  // Explain which processing is carried out by this procedure
  // Esercizio:  - Input
  // Percento:  - Input
  // Titolo:  - Input
  // Funzione:  - Input
  // Servizio:  - Input
  // CodInt:  - Input
  // Messaggio Non Bloccante:  - Input/Output
  // **********************************************************************
  public IDVariant ChkPercento12 (IDVariant Esercizio, IDVariant Percento, IDVariant Titolo, IDVariant Funzione, IDVariant Servizio, IDVariant CodInt, IDVariant MessaggioNonBloccante)
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;
    IDVariant v_RETVAL = new IDVariant(0,IDVariant.INTEGER);
    IDVariant v_IMP = new IDVariant(0,IDVariant.DECIMAL);
    IDVariant v_STANZ = new IDVariant(0,IDVariant.DECIMAL);
    IDVariant v_VARIMPORTO = new IDVariant(0,IDVariant.DECIMAL);

    try
    {
      TransCount = 0;
      // 
      // Chk Percento 12 Body
      // Procedure Body
      // 
      MainFrm.Cf4armDBObject.set_ErrorMessage((new IDVariant("")).stringValue());
      SQL = new StringBuffer();
      SQL.append("select ");
      SQL.append("  SUM(C.IMPORTO) as SUMIMPIMPORT, ");
      SQL.append("  SUM(D.STN_INI_CO + D.VARIAZIONI_CO) as SUBISTINCBVC, ");
      SQL.append("  SUM(B.IMPORTO) as SUMVARIMIMPO ");
      SQL.append("from ");
      SQL.append("  CAP A, ");
      SQL.append("  VARIMP B, ");
      SQL.append("  IMP C, ");
      SQL.append("  BIL D ");
      SQL.append("where (D.ESERCIZIO = " + IDL.CSql(Esercizio, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.ESERCIZIO = " + IDL.CSql(Esercizio, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (D.E_S = 'S') ");
      SQL.append("and   (A.E_S = 'S') ");
      SQL.append("and   (D.CAPITOLO = A.CAPITOLO) ");
      SQL.append("and   (D.ARTICOLO = A.ARTICOLO) ");
      SQL.append("and   (C.ANNO_IMP = " + IDL.CSql(Esercizio, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (C.CAPITOLO(+) = A.CAPITOLO) ");
      SQL.append("and   (C.ARTICOLO(+) = A.ARTICOLO) ");
      SQL.append("and   (NVL(TO_NUMBER(TO_CHAR(C.D_DATA_REG, 'mm' )), 0) < 7) ");
      SQL.append("and   (B.ANNO_IMP(+) = C.ANNO_IMP) ");
      SQL.append("and   (B.NUMERO_IMP(+) = C.NUMERO_IMP) ");
      SQL.append("and   (NVL(TO_NUMBER(TO_CHAR(B.D_DATA_REG, 'mm' )), 0) < 7) ");
      SQL.append("and   (A.TITOLO + 0 = " + IDL.CSql(Titolo, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.FUNZIONE + 0 = " + IDL.CSql(Funzione, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.SERVIZIO + 0 = " + IDL.CSql(Servizio, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.COD_INTERVENTO + 0 = " + IDL.CSql(CodInt, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
      if (!QV.EOF()) QV.MoveNext();
      if (!QV.EOF())
      {
        v_IMP = QV.Get("SUMIMPIMPORT", IDVariant.DECIMAL) ;
        v_STANZ = QV.Get("SUBISTINCBVC", IDVariant.DECIMAL) ;
        v_VARIMPORTO = QV.Get("SUMVARIMIMPO", IDVariant.DECIMAL) ;
      }
      QV.Close();
      if ((new IDVariant(MainFrm.Cf4armDBObject.ErrorMessage())).compareTo((new IDVariant("")), true)!=0)
      {
        IDVariant v_MESSAGGIO = new IDVariant(0,IDVariant.STRING);
        v_MESSAGGIO = (new IDVariant("Errore nel recupero dei dati dalla tabelle Impegni e Bilancio", IDVariant.STRING));
        MessaggioNonBloccante.set(new IDVariant(v_MESSAGGIO));
        v_RETVAL = (new IDVariant(-1));
      }
      else
      {
        IDVariant v_PERCENTO = new IDVariant(0,IDVariant.DECIMAL);
        v_PERCENTO = new IDVariant(IDL.Div(Percento, (new IDVariant(100))),IDVariant.DECIMAL);
        if (IDL.Mul(v_STANZ, v_PERCENTO).compareTo(IDL.Add(v_IMP, IDL.NullValue(v_VARIMPORTO,(new IDVariant(0)))), true)<0)
        {
          IDVariant v_SMS = new IDVariant(0,IDVariant.STRING);
          v_SMS = (new IDVariant("Il totale Impegnato  superiore al ", IDVariant.STRING));
          IDVariant v_SMS1 = new IDVariant(0,IDVariant.STRING);
          v_SMS1 = (new IDVariant("% dello Stanziamento", IDVariant.STRING));
          MessaggioNonBloccante.set(IDL.Add(v_SMS, IDL.Add(IDL.ToString(Percento), v_SMS1)));
          v_RETVAL = (new IDVariant(-1));
        }
        else
        {
          v_RETVAL = (new IDVariant(0));
        }
      }
      return v_RETVAL;
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Impegno", "ChkPercento12", _e);
      return new IDVariant();
    }
  }

