          IMDB.set_Value(IMDBDef1.TBL_PARAMETRI502, IMDBDef1.FLD_PARAMETRI502_ROWNAMEES, 0, (new IDVariant("S")));
          MainFrm.Show(MyGlb.FRM_SCEFATCONRIC, (new IDVariant(-1)).intValue(), this); 
