  // **********************************************************************
  // Proponi Opera
  // Explain which processing is carried out by this procedure
  // Esercizio:  - Input
  // Capitolo:  - Input
  // Articolo:  - Input
  // Opera:  - Input/Output
  // **********************************************************************
  public IDVariant ProponiOpera (IDVariant Esercizio, IDVariant Capitolo, IDVariant Articolo, IDVariant Opera)
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;
    IDVariant v_RETVAL = new IDVariant(0,IDVariant.INTEGER);
    IDVariant v_CONTATORE = new IDVariant(0,IDVariant.INTEGER);
    IDCachedRowSet C3;

    try
    {
      TransCount = 0;
      // 
      // Proponi Opera Body
      // Procedure Body
      // 
      IDVariant v_VCAPTOTFIN = new IDVariant(0,IDVariant.STRING);
      SQL = new StringBuffer();
      SQL.append("select ");
      SQL.append("  CHECK_CAP_TOT_FIN(" + IDL.CSql(Esercizio, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ",'S'," + IDL.CSql(Capitolo, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + "," + IDL.CSql(Articolo, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") as CHCATOFIESCA ");
      SQL.append("from ");
      SQL.append("  DUAL A ");
      QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
      if (!QV.EOF()) QV.MoveNext();
      if (!QV.EOF())
      {
        v_VCAPTOTFIN = QV.Get("CHCATOFIESCA", IDVariant.STRING) ;
      }
      QV.Close();
      // 
      // Solo se il capitolo  totalmente finanziato propongo
      // l'opera
      // 
      if (v_VCAPTOTFIN.equals((new IDVariant("SI")), true))
      {
        SQL = new StringBuffer();
        SQL.append("select distinct ");
        SQL.append("  A.OPERA as BILFINOPERA ");
        SQL.append("from ");
        SQL.append("  BIL_FIN A ");
        SQL.append("where (A.ESERCIZIO = " + IDL.CSql(Esercizio, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
        SQL.append("and   (A.CAPITOLO = " + IDL.CSql(Capitolo, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
        SQL.append("and   (A.ARTICOLO = " + IDL.CSql(Articolo, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
        SQL.append("and   (A.E_S = 'S') ");
        C3 = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
        if (!C3.EOF()) C3.MoveNext();
        while (!C3.EOF())
        {
          v_CONTATORE = IDL.Add(v_CONTATORE, (new IDVariant(1)));
          Opera.set(new IDVariant(C3.Get("BILFINOPERA")));
          C3.MoveNext();
        }
        C3.Close();
        v_RETVAL = new IDVariant(v_CONTATORE);
      }
      else
      {
        v_RETVAL = (new IDVariant(0));
      }
      return v_RETVAL;
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Impegno", "ProponiOpera", _e);
      return new IDVariant();
    }
  }

