  // **********************************************************************
  // Controlli Non Bloccanti Impegno
  // **********************************************************************
  public IDVariant ControlliNonBloccantiImpegno ()
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;
    IDVariant v_MESSANONBLOC = new IDVariant(0,IDVariant.STRING);

    try
    {
      TransCount = 0;
      // 
      // Controlli Non Bloccanti Impegno Body
      // Procedure Body
      // 
      v_MESSANONBLOC = (new IDVariant());
      // 
      // controllo servizi diretti
      // 
      {
        if (IDL.IsNull(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_SERVIZIO_CC, 0)))
        {
          IDVariant v_CONTATORE = new IDVariant(0,IDVariant.INTEGER);
          IDVariant v_TIPO = new IDVariant(0,IDVariant.STRING);
          SQL = new StringBuffer();
          SQL.append("select ");
          SQL.append("  COUNT(*) as COUNT1, ");
          SQL.append("  MIN(A.TIPO) as MINSERCOTIPO ");
          SQL.append("from ");
          SQL.append("  SERCOS A ");
          SQL.append("where (A.ESERCIZIO = " + IDL.CSql(IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSIOESERCI, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
          SQL.append("and   (A.E_S = 'S') ");
          SQL.append("and   (A.CAPITOLO = " + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_CAPITOLO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
          SQL.append("and   (A.ARTICOLO = " + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_ARTICOLO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
          QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
          if (!QV.EOF()) QV.MoveNext();
          if (!QV.EOF())
          {
            v_CONTATORE = QV.Get("COUNT1", IDVariant.INTEGER) ;
            v_TIPO = QV.Get("MINSERCOTIPO", IDVariant.STRING) ;
          }
          QV.Close();
          if (v_CONTATORE.compareTo((new IDVariant(0)), true)>0)
          {
            IDVariant v_MESSAGGIO = new IDVariant(0,IDVariant.STRING);
            IDVariant v_SMS = new IDVariant(0,IDVariant.STRING);
            v_SMS = (new IDVariant("Capitolo ", IDVariant.STRING));
            IDVariant v_SMS1 = new IDVariant(0,IDVariant.STRING);
            v_SMS1 = (new IDVariant(" collegata a Servizio ", IDVariant.STRING));
            IDVariant v_SMS2 = new IDVariant(0,IDVariant.STRING);
            v_SMS2 = (new IDVariant("a Dom. Individuale", IDVariant.STRING));
            IDVariant v_SMS3 = new IDVariant(0,IDVariant.STRING);
            v_SMS3 = (new IDVariant("in Economia", IDVariant.STRING));
            v_MESSAGGIO = IDL.Add(IDL.Add(IDL.Add(IDL.Add(v_SMS, IDL.ToString(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_CAPITOLO, 0))), (new IDVariant(" / "))), IDL.ToString(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_ARTICOLO, 0))), v_SMS1);
            if (v_TIPO.equals((new IDVariant("E")), true))
            {
              v_MESSAGGIO = IDL.Add(v_MESSAGGIO, v_SMS3);
            }
            else
            {
              v_MESSAGGIO = IDL.Add(v_MESSAGGIO, v_SMS2);
            }
            v_MESSANONBLOC = IDL.Add(IDL.Add(v_MESSANONBLOC, v_MESSAGGIO), (new IDVariant("<BR/>")));
          }
        }
      }
      if (PAN_IMPEGNO.IsNewRow() || (IDL.ToFloat(PAN_IMPEGNO.GetOrgValue(PFL_IMPEGNO_IMPORTO)).compareTo(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_IMPORTO, 0), true)!=0))
      {
        if (EsisteVariazioneNonEsecutive(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_CAPITOLO, 0), IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_ARTICOLO, 0)).compareTo((new IDVariant(0)), true)>0)
        {
          IDVariant v_MESSAGGIO = new IDVariant(0,IDVariant.STRING);
          v_MESSAGGIO = (new IDVariant("Attenzione! Sul Capitolo sono presenti variazioni non esecutive. ", IDVariant.STRING));
          v_MESSANONBLOC = IDL.Add(v_MESSANONBLOC, v_MESSAGGIO);
        }
      }
      if (MainFrm.FINANZIAMENT.equals((new IDVariant(-1)), true))
      {
        if (RigheFolderFinanziamenti().compareTo((new IDVariant(0)), true)>0)
        {
          if (!(IDL.IsNull(IMDB.Value(IMDBDef13.PQRY_OPERA4, IMDBDef13.PQSL_OPERA4_ROWNAMEOPERA, 0))))
          {
            if (IDL.NullValue((new IDVariant(PAN_OPERA.FieldText(PFL_OPERA_CUP2))),(new IDVariant(""))).compareTo((new IDVariant("")), true)!=0 && IDL.IsNull(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_CUP, 0)))
            {
              IDVariant v_MESSAGGIO = new IDVariant(0,IDVariant.STRING);
              v_MESSAGGIO = (new IDVariant("Nell'opera  indicato il CUP mentre sull'impegno no.", IDVariant.STRING));
              v_MESSANONBLOC = IDL.Add(IDL.Add(v_MESSANONBLOC, v_MESSAGGIO), (new IDVariant("<BR/>")));
            }
            else
            {
              if (IDL.NullValue(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_CUP, 0),(new IDVariant(""))).compareTo(IDL.NullValue((new IDVariant(PAN_OPERA.FieldText(PFL_OPERA_CUP2))),(new IDVariant(""))), true)!=0)
              {
                IDVariant v_MESSAGGIO = new IDVariant(0,IDVariant.STRING);
                v_MESSAGGIO = (new IDVariant("Cup dell'opera diverso da quello indicato sull'impegno.", IDVariant.STRING));
                v_MESSANONBLOC = IDL.Add(IDL.Add(v_MESSANONBLOC, v_MESSAGGIO), (new IDVariant("<BR/>")));
              }
            }
          }
        }
      }
      if (IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSCFPEATTI, 0).equals((new IDVariant(1)), true))
      {
        if (IDL.IsNull(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_PROGETTO_ID, 0)) && RecordPolElencoProgFC().compareTo((new IDVariant(0)), true)>0)
        {
          IDVariant v_AVVISO = new IDVariant(0,IDVariant.STRING);
          v_AVVISO = (new IDVariant("Il Capitolo  collegato a Progetto, ma il Progetto non  stato indicato", IDVariant.STRING));
          v_MESSANONBLOC = IDL.Add(IDL.Add(v_MESSANONBLOC, v_AVVISO), (new IDVariant("<BR/>")));
        }
      }
      // 
      // controllo voce tecnica
      // 
      if (!(IDL.IsNull(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_VOCE_TECNICA_OPERA, 0))))
      {
        if (IDL.ToInteger(PAN_IMPEGNO.GetOrgValue(PFL_IMPEGNO_VOCETECNOPER)).compareTo(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_VOCE_TECNICA_OPERA, 0), true)!=0)
        {
          IDVariant v_VCOUNT = new IDVariant(0,IDVariant.INTEGER);
          SQL = new StringBuffer();
          SQL.append("select ");
          SQL.append("  COUNT(*) as COUNT1 ");
          SQL.append("from ");
          SQL.append("  SUBIMP A ");
          SQL.append("where (A.ANNO_IMP = " + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_ANNO_IMP, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
          SQL.append("and   (A.NUMERO_IMP = " + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_NUMERO_IMP, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
          SQL.append("and   (NOT ((A.VOCE_TECNICA_OPERA IS NULL))) ");
          SQL.append("and   (A.VOCE_TECNICA_OPERA <> " + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_VOCE_TECNICA_OPERA, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
          QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
          if (!QV.EOF()) QV.MoveNext();
          if (!QV.EOF())
          {
            v_VCOUNT = QV.Get("COUNT1", IDVariant.INTEGER) ;
          }
          QV.Close();
          if (v_VCOUNT.compareTo((new IDVariant(0)), true)>0)
          {
            IDVariant v_AVVISO = new IDVariant(0,IDVariant.STRING);
            v_AVVISO = (new IDVariant("Si ricorda che, alla conferma, i subimpegni con voce tecnica diversa verranno aggiornati con quella dell'impegno."));
            v_MESSANONBLOC = IDL.Add(IDL.Add(v_MESSANONBLOC, v_AVVISO), (new IDVariant("<BR/>")));
          }
        }
      }
      return v_MESSANONBLOC;
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Impegno", "ControlliNonBloccantiImpegno", _e);
      return new IDVariant();
    }
  }

