  // **********************************************************************
  // Finanziamento Fin
  // Explain which processing is carried out by this procedure
  // Capitolo:  - Input
  // Articolo:  - Input
  // Esercizio:  - Input
  // **********************************************************************
  public IDVariant FinanziamentoFin (IDVariant Capitolo, IDVariant Articolo, IDVariant Esercizio)
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;
    IDVariant v_IMPORTOFIN = new IDVariant(0,IDVariant.DECIMAL);
    IDVariant v_IMPORTOVARCO = new IDVariant(0,IDVariant.DECIMAL);
    IDVariant v_IMPBIL = new IDVariant(0,IDVariant.DECIMAL);
    IDVariant v_RETVAL = new IDVariant(0,IDVariant.INTEGER);

    try
    {
      TransCount = 0;
      // 
      // Finanziamento Fin Body
      // Procedure Body
      // 
      SQL = new StringBuffer();
      SQL.append("select ");
      SQL.append("  SUM(A.STN_INI_CO) as SUBIFISTINCO ");
      SQL.append("from ");
      SQL.append("  BIL_FIN A ");
      SQL.append("where (A.E_S = 'S') ");
      SQL.append("and   (A.ESERCIZIO = " + IDL.CSql(IMDB.Value(IMDBDef4.TBL_PARAMETRI20, IMDBDef4.FLD_PARAMETRI20_RONAANPEBIFI, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.CAPITOLO = " + IDL.CSql(Capitolo, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.ARTICOLO = " + IDL.CSql(Articolo, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
      if (!QV.EOF()) QV.MoveNext();
      if (!QV.EOF())
      {
        v_IMPORTOFIN = QV.Get("SUBIFISTINCO", IDVariant.DECIMAL) ;
      }
      QV.Close();
      SQL = new StringBuffer();
      SQL.append("select ");
      SQL.append("  SUM(A.IMPORTO) as SUMVARCOIMPO ");
      SQL.append("from ");
      SQL.append("  VARCOM A ");
      SQL.append("where (A.E_S = 'S') ");
      SQL.append("and   (A.ESERCIZIO = " + IDL.CSql(Esercizio, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.CAPITOLO = " + IDL.CSql(Capitolo, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.ARTICOLO = " + IDL.CSql(Articolo, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (NOT ((A.FINANZIAMENTO IS NULL))) ");
      QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
      if (!QV.EOF()) QV.MoveNext();
      if (!QV.EOF())
      {
        v_IMPORTOVARCO = QV.Get("SUMVARCOIMPO", IDVariant.DECIMAL) ;
      }
      QV.Close();
      SQL = new StringBuffer();
      SQL.append("select ");
      SQL.append("  SUM(A.STN_INI_CO + A.VARIAZIONI_CO) as SUBISTINCBVC ");
      SQL.append("from ");
      SQL.append("  BIL A ");
      SQL.append("where (A.E_S = 'S') ");
      SQL.append("and   (A.ESERCIZIO = " + IDL.CSql(Esercizio, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.CAPITOLO = " + IDL.CSql(Capitolo, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.ARTICOLO = " + IDL.CSql(Articolo, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
      if (!QV.EOF()) QV.MoveNext();
      if (!QV.EOF())
      {
        v_IMPBIL = QV.Get("SUBISTINCBVC", IDVariant.DECIMAL) ;
      }
      QV.Close();
      if (IDL.Add(IDL.NullValue(v_IMPORTOFIN,(new IDVariant(0))), IDL.NullValue(v_IMPORTOVARCO,(new IDVariant(0)))).equals(IDL.NullValue(v_IMPBIL,(new IDVariant(0))), true))
      {
        v_RETVAL = (new IDVariant(0));
      }
      else
      {
        if (IDL.Add(IDL.NullValue(v_IMPORTOFIN,(new IDVariant(0))), IDL.NullValue(v_IMPORTOVARCO,(new IDVariant(0)))).compareTo(IDL.NullValue(v_IMPBIL,(new IDVariant(0))), true)<0)
        {
          v_RETVAL = (new IDVariant(1));
        }
        else
        {
          v_RETVAL = (new IDVariant(-1));
        }
      }
      return v_RETVAL;
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Impegno", "FinanziamentoFin", _e);
      return new IDVariant();
    }
  }

