  // 
  // Cancel: Parametro di output booleano, diventato obsoleto. Per segnalare una condizione di errore sul campo  necessario utilizzare la funzione SetErrorText. - Input/Output
  // **********************************************************************
  private void PFL_FINANZIAMENT_CODICEFINAZI_ValidateCell(IDVariant Cancel)
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;

    try
    {
      TransCount = 0;
      // 
      // Finanziamenti Codice Finaziamento Validate Event Body
      // Procedure Body
      // 
      if (IMDB.Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_FINANZIAMENTO, 0).compareTo(IMDB.Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_IMPFINCOFIOL, 0), true)!=0)
      {
        if (!(IDL.IsNull(IMDB.Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_FINANZIAMENTO, 0))))
        {
          IDVariant v_DESCRIZIONE = new IDVariant(0,IDVariant.STRING);
          v_DESCRIZIONE = (new IDVariant());
          SQL = new StringBuffer();
          SQL.append("select ");
          SQL.append("  A.DESCRIZIONE as FINANZDESCRI ");
          SQL.append("from ");
          SQL.append("  FINANZIAMENTI A ");
          SQL.append("where (A.CODICE = " + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_FINANZIAMENTO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
          QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
          if (!QV.EOF()) QV.MoveNext();
          if (!QV.EOF())
          {
            v_DESCRIZIONE = QV.Get("FINANZDESCRI", IDVariant.STRING) ;
          }
          QV.Close();
          if (IDL.IsNull(v_DESCRIZIONE) || v_DESCRIZIONE.equals((new IDVariant("")), true))
          {
            IDVariant v_MESSAGGIO = new IDVariant(0,IDVariant.STRING);
            v_MESSAGGIO = (new IDVariant("Finanziamento non presente", IDVariant.STRING));
            MainFrm.set_AlertMessage(v_MESSAGGIO); 
            IMDB.set_Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_FINANZIAMENTO, 0, (new IDVariant()));
            IMDB.set_Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_IMPFINCOFIOL, 0, (new IDVariant()));
            return;
          }
          else
          {
            IDVariant v_CONTATORE = new IDVariant(0,IDVariant.DECIMAL);
            SQL = new StringBuffer();
            SQL.append("select ");
            SQL.append("  COUNT(*) as COUNT1 ");
            SQL.append("from ");
            SQL.append("  BIL_FIN A ");
            SQL.append("where (A.ESERCIZIO = " + IDL.CSql(IMDB.Value(IMDBDef4.TBL_PARAMETRI20, IMDBDef4.FLD_PARAMETRI20_RONAANPEBIFI, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
            SQL.append("and   (A.E_S = DECODE(" + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_S, 0), IDL.FMT_CHAR, MainFrm.Cf4armDBObject.DBO()) + ", NULL, A.E_S, " + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_S, 0), IDL.FMT_CHAR, MainFrm.Cf4armDBObject.DBO()) + ")) ");
            SQL.append("and   (A.CAPITOLO = DECODE(" + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_CAPITOLO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ", -1, A.CAPITOLO, " + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_CAPITOLO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ")) ");
            SQL.append("and   (A.ARTICOLO = DECODE(" + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_ARTICOLO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ", -1, A.ARTICOLO, " + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_ARTICOLO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ")) ");
            SQL.append("and   (A.OPERA = DECODE(NVL(" + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_OPERA4, IMDBDef13.PQSL_OPERA4_ROWNAMEOPERA, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ", " + IDL.CSql(IMDB.Value(IMDBDef4.TBL_OPERA, IMDBDef4.FLD_OPERA_ROWNAMEOPERA, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + "), -1, A.OPERA, NVL(" + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_OPERA4, IMDBDef13.PQSL_OPERA4_ROWNAMEOPERA, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ", " + IDL.CSql(IMDB.Value(IMDBDef4.TBL_OPERA, IMDBDef4.FLD_OPERA_ROWNAMEOPERA, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + "))) ");
            SQL.append("and   (A.FINANZIAMENTO = DECODE(" + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_FINANZIAMENTO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ", -1, A.FINANZIAMENTO, " + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_FINANZIAMENTO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ")) ");
            QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
            if (!QV.EOF()) QV.MoveNext();
            if (!QV.EOF())
            {
              v_CONTATORE = QV.Get("COUNT1", IDVariant.DECIMAL) ;
            }
            QV.Close();
            if (v_CONTATORE.compareTo((new IDVariant(0)), true)<=0)
            {
              IDVariant v_MESSAGGIO = new IDVariant(0,IDVariant.STRING);
              v_MESSAGGIO = (new IDVariant("Sul Capitolo non sono presenti previsioni per il finanziamento e l'opera indicati", IDVariant.STRING));
              MainFrm.set_AlertMessage(v_MESSAGGIO); 
              IMDB.set_Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_FINANZIAMENTO, 0, (new IDVariant()));
              IMDB.set_Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_IMPFINCOFIOL, 0, (new IDVariant()));
              return;
            }
          }
        }
        IMDB.set_Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_IMPFINCOFIOL, 0, IMDB.Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_FINANZIAMENTO, 0));
      }
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Impegno", "FinanziamentiCodiceFinaziamentoValidateEvent", _e);
    }
  }

