  // **********************************************************************
  // Proponi Finanziamento
  // **********************************************************************
  public int ProponiFinanziamento ()
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;

    try
    {
      TransCount = 0;
      // 
      // Proponi Finanziamento Body
      // Procedure Body
      // 
      IDVariant v_VCAPTOTFIN = new IDVariant(0,IDVariant.STRING);
      SQL = new StringBuffer();
      SQL.append("select ");
      SQL.append("  CHECK_CAP_TOT_FIN(" + IDL.CSql(IMDB.Value(IMDBDef4.TBL_PARAMETRI20, IMDBDef4.FLD_PARAMETRI20_RONAANPEBIFI, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ",'S'," + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_CAPITOLO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + "," + IDL.CSql(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_ARTICOLO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") as CCTFRNAPBFSI ");
      SQL.append("from ");
      SQL.append("  DUAL A ");
      QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
      if (!QV.EOF()) QV.MoveNext();
      if (!QV.EOF())
      {
        v_VCAPTOTFIN = QV.Get("CCTFRNAPBFSI", IDVariant.STRING) ;
      }
      QV.Close();
      // 
      // Sole se il capitolo  totalmente finanziato propongo
      // il finanziamento
      // 
      if ((new IDVariant(PAN_FINANZIAMENT.GetNumRows())).compareTo((new IDVariant(0)), true)>=0 && (new IDVariant(PAN_FINANZIAMENT.ActualPosition(true))).equals((new IDVariant(1)), true) && v_VCAPTOTFIN.equals((new IDVariant("SI")), true))
      {
        if (PAN_FINANZIAMENT.IsNewRow())
        {
          // 
          // proposta finanziamento
          // 
          {
            IDVariant v_FINANZIAMENT = new IDVariant(0,IDVariant.INTEGER);
            if (IDL.NullValue(IDL.NullValue(IMDB.Value(IMDBDef13.PQRY_OPERA4, IMDBDef13.PQSL_OPERA4_ROWNAMEOPERA, 0),IMDB.Value(IMDBDef4.TBL_OPERA, IMDBDef4.FLD_OPERA_ROWNAMEOPERA, 0)),(new IDVariant(-1))).compareTo((new IDVariant(-1)), true)!=0)
            {
              if (ContaBilFinOpe(IMDB.Value(IMDBDef4.TBL_PARAMETRI20, IMDBDef4.FLD_PARAMETRI20_RONAANPEBIFI, 0), (new IDVariant("S")), IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_CAPITOLO, 0), IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_ARTICOLO, 0), IDL.NullValue(IMDB.Value(IMDBDef13.PQRY_OPERA4, IMDBDef13.PQSL_OPERA4_ROWNAMEOPERA, 0),IMDB.Value(IMDBDef4.TBL_OPERA, IMDBDef4.FLD_OPERA_ROWNAMEOPERA, 0)), v_FINANZIAMENT).equals((new IDVariant(1)), true))
              {
                IMDB.set_Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_FINANZIAMENTO, 0, new IDVariant(v_FINANZIAMENT));
                if (IDL.NullValue(IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_IMPORTO, 0),(new IDVariant(-1))).compareTo((new IDVariant(-1)), true)!=0)
                {
                  if (IDL.IsNull(IMDB.Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_IMPORTO, 0)))
                  {
                    IMDB.set_Value(IMDBDef13.PQRY_IMPFIN2, IMDBDef13.PQSL_IMPFIN2_IMPORTO, 0, IMDB.Value(IMDBDef13.PQRY_IMP7, IMDBDef13.PQSL_IMP7_IMPORTO, 0));
                  }
                }
                PAN_FINANZIAMENT.UpdatePanel(MainFrm);
              }
            }
            else
            {
              IDVariant v_MESSAGGIO = new IDVariant(0,IDVariant.STRING);
              v_MESSAGGIO = (new IDVariant("Non  stata indicata l'opera per il finanziamento", IDVariant.STRING));
              if (IMDB.Value(IMDBDef4.TBL_PARAMETRVAR5, IMDBDef4.FLD_PARAMETRVAR5_RONACHSEINOP, 0).equals((new IDVariant(1)), true))
              {
                MainFrm.set_AlertMessage(v_MESSAGGIO); 
              }
              return 0;
            }
          }
        }
      }
      return 0;
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("Impegno", "ProponiFinanziamento", _e);
      return -1;
    }
  }

