  private void PAN_FINANZIAMENT_InitQueries()
  {
    StringBuffer SQL;

    PAN_FINANZIAMENT.SetSize(MyGlb.OBJ_QUERY, 2);
    SQL = new StringBuffer();
    SQL.append("select ");
    SQL.append("  A.DESCRIZIONE as FINANZDESCRI, ");
    SQL.append("  'I' as INFOFINANZIA ");
    SQL.append("from ");
    SQL.append("  FINANZIAMENTI A ");
    SQL.append("where (A.CODICE = ~~FINANZIAMENTO~~) ");
    PAN_FINANZIAMENT.SetQuery(PPQRY_FINANZIAMEN1, 0, SQL, -1, "");
    PAN_FINANZIAMENT.SetQueryDB(PPQRY_FINANZIAMEN1, MainFrm.Cf4armDBObject.DB, 4);
    PAN_FINANZIAMENT.SetMasterTable(PPQRY_FINANZIAMEN1, "FINANZIAMENTI");
    PAN_FINANZIAMENT.SetIMDB(IMDB, "PQRY_IMPFIN2", true);
    PAN_FINANZIAMENT.set_SetString(MyGlb.MASTER_ROWNAME, "IMP FIN");
    SQL = new StringBuffer();
    SQL.append("select ");
    SQL.append("  A.ANNO_IMP as ANNO_IMP, ");
    SQL.append("  A.NUMERO_IMP as NUMERO_IMP, ");
    SQL.append("  A.FINANZIAMENTO as FINANZIAMENTO, ");
    SQL.append("  A.FINANZIAMENTO as IMPFINCOFIOL, ");
    SQL.append("  A.OPERA as OPERA, ");
    SQL.append("  A.IMPORTO as IMPORTO, ");
    SQL.append("  A.CAPITOLO as CAPITOLO, ");
    SQL.append("  A.ARTICOLO as ARTICOLO, ");
    SQL.append("  A.S as S ");
    PAN_FINANZIAMENT.SetQuery(PPQRY_IMPFIN2, 0, SQL, -1, "");
    SQL = new StringBuffer();
    SQL.append("from ");
    SQL.append("  IMP_FIN A ");
    PAN_FINANZIAMENT.SetQuery(PPQRY_IMPFIN2, 1, SQL, -1, "");
    SQL = new StringBuffer();
    SQL.append("where (A.ANNO_IMP = ~~PQRY_IMP7.ANNO_IMP~~) ");
    SQL.append("and   (A.NUMERO_IMP = ~~PQRY_IMP7.NUMERO_IMP~~) ");
    PAN_FINANZIAMENT.SetQuery(PPQRY_IMPFIN2, 2, SQL, -1, "");
    SQL = new StringBuffer();
    PAN_FINANZIAMENT.SetQuery(PPQRY_IMPFIN2, 3, SQL, -1, "");
    SQL = new StringBuffer();
    PAN_FINANZIAMENT.SetQuery(PPQRY_IMPFIN2, 4, SQL, -1, "");
    SQL = new StringBuffer();
    SQL.append("order by ");
    SQL.append("  A.FINANZIAMENTO ");
    PAN_FINANZIAMENT.SetQuery(PPQRY_IMPFIN2, 5, SQL, -1, "");
    PAN_FINANZIAMENT.SetQueryDB(PPQRY_IMPFIN2, MainFrm.Cf4armDBObject.DB, 4);
    PAN_FINANZIAMENT.SetMasterTable(0, "IMP_FIN");
    PAN_FINANZIAMENT.AddToSortList(PFL_FINANZIAMENT_CODICEFINAZI, true);
    //
    // Rieseguo la query se il pannello era gi in stato DATA
    if (PAN_FINANZIAMENT.Status() == 2)
    {
      int oldListQBE = PAN_FINANZIAMENT.iUseListQBE;
      PAN_FINANZIAMENT.iUseListQBE = 0;
      PAN_FINANZIAMENT.PanelCommand(Glb.PCM_SEARCH);
      PAN_FINANZIAMENT.PanelCommand(Glb.PCM_FIND);
      PAN_FINANZIAMENT.iUseListQBE = oldListQBE;
    }
  }

