  private void PAN_OPERA_InitQueries()
  {
    StringBuffer SQL;

    PAN_OPERA.SetSize(MyGlb.OBJ_QUERY, 3);
    SQL = new StringBuffer();
    SQL.append("select ");
    SQL.append("  A.DESCRIZIONE as OPEREDESCRIZ, ");
    SQL.append("  A.CUP as OPERECUP ");
    SQL.append("from ");
    SQL.append("  OPERE A ");
    SQL.append("where (A.CODICE = ~~ROWNAMEOPERA~~) ");
    PAN_OPERA.SetQuery(PPQRY_OPERE, 0, SQL, -1, "");
    PAN_OPERA.SetQueryDB(PPQRY_OPERE, MainFrm.Cf4armDBObject.DB, 4);
    PAN_OPERA.SetMasterTable(PPQRY_OPERE, "OPERE");
    SQL = new StringBuffer();
    SQL.append("select ");
    SQL.append("  A.DESCRIZIONE as VOCTECOPEDES ");
    SQL.append("from ");
    SQL.append("  VOCI_TECNICHE_OPERE A ");
    SQL.append("where (A.CODICE = ~~ROWNAMVOCTEC~~) ");
    PAN_OPERA.SetQuery(PPQRY_VOCITECNOPER, 0, SQL, -1, "");
    PAN_OPERA.SetQueryDB(PPQRY_VOCITECNOPER, MainFrm.Cf4armDBObject.DB, 4);
    PAN_OPERA.SetMasterTable(PPQRY_VOCITECNOPER, "VOCI_TECNICHE_OPERE");
    PAN_OPERA.SetIMDB(IMDB, "PQRY_OPERA4", true);
    PAN_OPERA.set_SetString(MyGlb.MASTER_ROWNAME, "Row Name");
    PAN_OPERA.SetQueryIMDB(PPQRY_OPERA4, IMDBDef13.PQRY_OPERA4_RS, IMDBDef4.TBL_OPERA);
    JustLoaded = true;
    PAN_OPERA.SetFieldPrimaryIndex(PFL_OPERA_OPERA3, IMDBDef4.FLD_OPERA_ROWNAMEOPERA);
    PAN_OPERA.SetFieldPrimaryIndex(PFL_OPERA_VOCETECNICA, IMDBDef4.FLD_OPERA_ROWNAMVOCTEC);
    PAN_OPERA.SetMasterTable(0, "OPERA");
    //
    // Rieseguo la query se il pannello era gi in stato DATA
    if (PAN_OPERA.Status() == 2)
    {
      int oldListQBE = PAN_OPERA.iUseListQBE;
      PAN_OPERA.iUseListQBE = 0;
      PAN_OPERA.PanelCommand(Glb.PCM_SEARCH);
      PAN_OPERA.PanelCommand(Glb.PCM_FIND);
      PAN_OPERA.iUseListQBE = oldListQBE;
    }
  }

  
  
