  // **********************************************************************
  public IDVariant ControlloTipoUO ()
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;
    IDVariant v_GESTIONE = new IDVariant(0,IDVariant.INTEGER);
    IDVariant v_UTILIZZO = new IDVariant(0,IDVariant.INTEGER);
    IDVariant v_AVVISO = new IDVariant(0,IDVariant.STRING);
    IDCachedRowSet C2;

    try
    {
      TransCount = 0;
      // 
      // Controllo Tipo UO Body
      // Procedure Body
      // 
      v_AVVISO = (new IDVariant());
      SQL = new StringBuffer();
      SQL.append("select ");
      SQL.append("  A.TIPO_UNITA_ORGANIZZATIVA as CAPUOTIPUNOR, ");
      SQL.append("  A.SCADENZA as CAPUOSCADENZ ");
      SQL.append("from ");
      SQL.append("  CAP_UO A ");
      SQL.append("where (A.ESERCIZIO = " + IDL.CSql(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_ESERCIZIO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.E_S = " + IDL.CSql(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_E_S, 0), IDL.FMT_CHAR, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.CAPITOLO = " + IDL.CSql(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_CAPITOLO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (A.ARTICOLO = " + IDL.CSql(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_ARTICOLO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      C2 = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
      if (!C2.EOF()) C2.MoveNext();
      while (!C2.EOF())
      {
        if (C2.Get("CAPUOTIPUNOR").equals((new IDVariant("G")), true))
        {
          if (IDL.IsNull(C2.Get("CAPUOSCADENZ")) || C2.Get("CAPUOSCADENZ").compareTo(IDL.Today(), true)>0)
          {
            v_GESTIONE = (new IDVariant(-1));
          }
        }
        if (C2.Get("CAPUOTIPUNOR").equals((new IDVariant("U")), true))
        {
          if (IDL.IsNull(C2.Get("CAPUOSCADENZ")) || C2.Get("CAPUOSCADENZ").compareTo(IDL.Today(), true)>0)
          {
            v_UTILIZZO = (new IDVariant(-1));
          }
        }
        C2.MoveNext();
      }
      C2.Close();
      if (!(v_GESTIONE.booleanValue()))
      {
        IDVariant v_AVVISO1 = new IDVariant(0,IDVariant.STRING);
        v_AVVISO1 = (new IDVariant("Non sono state associate Unità Organizzative di Gestione!", IDVariant.STRING));
        v_AVVISO = IDL.Add(v_AVVISO, v_AVVISO1);
      }
      if (!(v_UTILIZZO.booleanValue()))
      {
        IDVariant v_AVVISO2 = new IDVariant(0,IDVariant.STRING);
        v_AVVISO2 = (new IDVariant("Non sono state associate Unità Organizzative di Utilizzo!", IDVariant.STRING));
        v_AVVISO = IDL.Add(IDL.Add(v_AVVISO, (new IDVariant("<BR/>"))), v_AVVISO2);
      }
      return v_AVVISO;
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("InserimentoAggiornamentoPDConti", "ControlloTipoUO", _e);
      return new IDVariant();
    }
  }

