  // **********************************************************************
  // Initcodice Gestionale
  // Explain which processing is carried out by this procedure
  // **********************************************************************
  public int InitcodiceGestionale ()
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;
    IDVariant v_NUMREC = new IDVariant(0,IDVariant.INTEGER);
    IDVariant v_CODICEGESTIO = new IDVariant(0,IDVariant.INTEGER);

    try
    {
      TransCount = 0;
      // 
      // Initcodice Gestionale Body
      // Procedure Body
      // 
      SQL = new StringBuffer();
      SQL.append("select ");
      SQL.append("  COUNT(*) as COUNT1, ");
      SQL.append("  MIN(A.CODICE_GESTIONALE) as MINRICGECOGE ");
      SQL.append("from ");
      SQL.append("  RICLASSIFICAZIONI_GEST A, ");
      SQL.append("  RICLASSIFICAZIONI B, ");
      SQL.append("  CODICI_GESTIONALI C ");
      SQL.append("where (C.CODICE = A.CODICE_GESTIONALE) ");
      SQL.append("and   (A.PROGR_RICLASSIFICAZIONI = B.PROGRESSIVO) ");
      SQL.append("and   (C.E_S = B.E_S) ");
      SQL.append("and   (B.E_S = " + IDL.CSql(IMDB.Value(IMDBDef3.TBL_PARAMETRI287, IMDBDef3.FLD_PARAMETRI287_ROWNAMENTSPE, 0), IDL.FMT_CHAR, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (B.TITOLO = " + IDL.CSql(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_TITOLO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   (NVL(B.CATEGORIA, -1) = NVL(" + IDL.CSql(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_CATEGORIA, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ", NVL(B.CATEGORIA, -1))) ");
      SQL.append("and   (NVL(B.COD_INTERVENTO, -1) = NVL(" + IDL.CSql(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_COD_INTERVENTO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ", NVL(B.COD_INTERVENTO, -1))) ");
      SQL.append("and   (NVL(B.COD_TERZI, -1) = NVL(" + IDL.CSql(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_COD_TERZI, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ", NVL(B.COD_TERZI, -1))) ");
      SQL.append("and   (B.VOCE_ECON = " + IDL.CSql(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_VOCE_ECON, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      SQL.append("and   ((C.SCADENZA IS NULL) OR C.SCADENZA >= " + IDL.CSql(IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSIOESERCI, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
      QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
      if (!QV.EOF()) QV.MoveNext();
      if (!QV.EOF())
      {
        v_NUMREC = QV.Get("COUNT1", IDVariant.INTEGER) ;
        v_CODICEGESTIO = QV.Get("MINRICGECOGE", IDVariant.INTEGER) ;
      }
      QV.Close();
      if (v_NUMREC.equals((new IDVariant(1)), true))
      {
        IMDB.set_Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_CODICE_GESTIONALE, 0, new IDVariant(v_CODICEGESTIO));
      }
      else
      {
        IMDB.set_Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_CODICE_GESTIONALE, 0, (new IDVariant()));
      }
      return 0;
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("InserimentoAggiornamentoPDConti", "InitcodiceGestionale", _e);
      return -1;
    }
  }

