      SQL.append("and   (NVL(B.COD_INTERVENTO, -1) = NVL(" + IDL.CSql(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_COD_INTERVENTO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ", NVL(B.COD_INTERVENTO, -1))) ");
