  // Cancel: Non utilizzare questo argomento. Per segnalare una condizione di errore, utilizzare le funzioni SetErrorText del campo di pannello o del pannello stesso. - Input/Output
  // **********************************************************************
  private void PAN_PIANODEICONT_OnUpdatingRow(IDVariant Column, IDVariant FieldModified, IDVariant FieldWasModified, IDVariant RowWasModified, IDVariant Inserting, IDVariant Cancel)
  {
    StringBuffer SQL = new StringBuffer();
    int TransCount   = 0;
    int ReturnStatus = 0;
    IDCachedRowSet QV;

    try
    {
      TransCount = 0;
      // 
      // Piano Dei Conti On Updating Row Event Body
      // Procedure Body
      // 
      if (Inserting.booleanValue())
      {
        // 
        // Nuovo Record posizionato su Capitolo
        // 
        if (IDL.IsNull(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_ARTICOLO, 0)))
        {
          if (IMDB.Value(IMDBDef3.TBL_PARAMETRI287, IMDBDef3.FLD_PARAMETRI287_ROWNAMETIPON, 0).equals((new IDVariant(4)), true))
          {
            PAN_PIANODEICONT.SetFlags (Glb.OBJ_FIELD, PFL_PIANODEICONT_CAPITOLO1, ((new IDVariant(-1)).booleanValue())? Glb.OBJ_VISIBLE : 0, Glb.OBJ_VISIBLE); 
            PAN_PIANODEICONT.SetFlags (Glb.OBJ_FIELD, PFL_PIANODEICONT_CAPITOLO1, ((new IDVariant(-1)).booleanValue())? Glb.OBJ_ENABLED : 0, Glb.OBJ_ENABLED); 
            IMDB.set_Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_ARTICOLO, 0, (new IDVariant(0)));
            Caricapadre();
          }
        }
      }
      if (IDL.IsNull(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_ESERCIZIO, 0)))
      {
        IMDB.set_Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_ESERCIZIO, 0, IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSIOESERCI, 0));
      }
      if (IDL.IsNull(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_E_S, 0)))
      {
        IMDB.set_Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_E_S, 0, IMDB.Value(IMDBDef3.TBL_PARAMETRI287, IMDBDef3.FLD_PARAMETRI287_ROWNAMENTSPE, 0));
      }
      if (Inserting.equals((new IDVariant(-1)), true))
      {
        if (IDL.IsNull(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_UTENTE_INSERIMENTO, 0)))
        {
          IMDB.set_Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_UTENTE_INSERIMENTO, 0, IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSUSERNAME, 0));
        }
        if (IDL.IsNull(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_DATA_INSERIMENTO, 0)))
        {
          IMDB.set_Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_DATA_INSERIMENTO, 0, IDL.Today());
        }
      }
      else
      {
        if (IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_UTENTE_ULTIMO_AGG, 0).compareTo(IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSUSERNAME, 0), true)!=0)
        {
          IMDB.set_Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_UTENTE_ULTIMO_AGG, 0, IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSUSERNAME, 0));
        }
        if (IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_DATA_ULTIMO_AGG, 0).compareTo(IDL.Today(), true)!=0)
        {
          IMDB.set_Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_DATA_ULTIMO_AGG, 0, IDL.Today());
        }
      }
      if (IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_DESCRIZIONE, 0).compareTo((new IDVariant()), true)!=0 && IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_DESCRIZIONE_ABBREVIATA, 0).equals((new IDVariant()), true))
      {
        IMDB.set_Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_DESCRIZIONE_ABBREVIATA, 0, IDL.SubStr(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_DESCRIZIONE, 0), (new IDVariant(0)), (new IDVariant(40))));
      }
      if (Column.equals((new IDVariant(PFL_PIANODEICONT_TIPOVINCOLO)), true) && FieldWasModified.booleanValue())
      {
        if (!(IDL.IsNull(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_TIPO_VINCOLO, 0))))
        {
          IDVariant v_VTIPOAVANZO = null;
          v_VTIPOAVANZO = (new IDVariant());
          SQL = new StringBuffer();
          SQL.append("select ");
          SQL.append("  A.TIPO_AVANZO as VINCTIPOAVAN ");
          SQL.append("from ");
          SQL.append("  VINCOLI A ");
          SQL.append("where (A.CODICE = " + IDL.CSql(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_TIPO_VINCOLO, 0), IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
          QV = MainFrm.Cf4armDBObject.DBO().OpenRS(SQL);
          if (!QV.EOF()) QV.MoveNext();
          if (!QV.EOF())
          {
            v_VTIPOAVANZO = QV.Get("VINCTIPOAVAN", IDVariant.DECIMAL) ;
          }
          QV.Close();
          IMDB.set_Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_TIPO_AVANZO, 0, new IDVariant(v_VTIPOAVANZO));
        }
        else
        {
          IMDB.set_Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_TIPO_AVANZO, 0, (new IDVariant()));
        }
      }
    }
    catch (Exception _e)
    {
      MainFrm.ErrObj.ProcError ("InserimentoAggiornamentoPDConti", "PianoDeiContiOnUpdatingRowEvent", _e);
    }
  }

