  private void INSEAGGPDCON_CAPUO5() throws SQLException
  {
    IDVariant[] AggrBuff;
    int[] AggrRowCount;
    int AggrCount=0;
    boolean AggrNewGroup = false;

    IMDB.TblTruncate(IMDBDef10.PQRY_CAPUO5_RS);
    IMDB.TblMoveFirst(IMDBDef3.TBL_CAPUOIMDB2, 0);
    Loop1: while (!IMDB.Eof(IMDBDef3.TBL_CAPUOIMDB2, 0))
    {
      if (IMDB.Value(IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_ESERCIZIO, 0).equals(IMDB.Value(IMDBDef7.TBL_DATISESSIONE, IMDBDef7.FLD_DATISESSIONE_SESSIOESERCI, 0), true))
      {
        if (IMDB.Value(IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_E_S, 0).equals(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_E_S, 0), true))
        {
          if (IMDB.Value(IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_CAPITOLO, 0).equals(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_CAPITOLO, 0), true))
          {
            if (IMDB.Value(IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_ARTICOLO, 0).equals(IMDB.Value(IMDBDef10.PQRY_CAP16, IMDBDef10.PQSL_CAP16_ARTICOLO, 0), true))
            {
              IMDB.TblAddNew(IMDBDef10.PQRY_CAPUO5_RS, 0);
              IMDB.TblLinkRow(IMDBDef10.PQRY_CAPUO5_RS, 0, IMDBDef3.TBL_CAPUOIMDB2, 0);
              IMDB.TblLinkField(IMDBDef10.PQRY_CAPUO5_RS, 0, 0, IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_SCADENZA, 0);
              IMDB.TblLinkField(IMDBDef10.PQRY_CAPUO5_RS, 1, 0, IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_TIPO_UNITA_ORGANIZZATIVA, 0);
              IMDB.TblLinkField(IMDBDef10.PQRY_CAPUO5_RS, 2, 0, IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_PROGR_UNITA_ORGANIZZATIVA, 0);
              IMDB.TblLinkField(IMDBDef10.PQRY_CAPUO5_RS, 3, 0, IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_UTENTE_INSERIMENTO, 0);
              IMDB.TblLinkField(IMDBDef10.PQRY_CAPUO5_RS, 4, 0, IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_DATA_INSERIMENTO, 0);
              IMDB.TblLinkField(IMDBDef10.PQRY_CAPUO5_RS, 5, 0, IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_UTENTE_ULTIMO_AGG, 0);
              IMDB.TblLinkField(IMDBDef10.PQRY_CAPUO5_RS, 6, 0, IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_DATA_ULTIMO_AGG, 0);
              IMDB.set_Value(IMDBDef10.PQRY_CAPUO5_RS, 7, 0, IDL.NullValue(IMDB.Value(IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_SCADENZA, 0),(new IDVariant("1900/01/01# 00:00:00", IDVariant.DATETIME))));
              IMDB.TblLinkField(IMDBDef10.PQRY_CAPUO5_RS, 8, 0, IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_ESERCIZIO, 0);
              IMDB.TblLinkField(IMDBDef10.PQRY_CAPUO5_RS, 9, 0, IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_E_S, 0);
              IMDB.TblLinkField(IMDBDef10.PQRY_CAPUO5_RS, 10, 0, IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_CAPITOLO, 0);
              IMDB.TblLinkField(IMDBDef10.PQRY_CAPUO5_RS, 11, 0, IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_ARTICOLO, 0);
              IMDB.TblLinkField(IMDBDef10.PQRY_CAPUO5_RS, 12, 0, IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_CAPUOIMDBSTA, 0);
              IMDB.TblLinkField(IMDBDef10.PQRY_CAPUO5_RS, 13, 0, IMDBDef3.TBL_CAPUOIMDB2, IMDBDef3.FLD_CAPUOIMDB2_PREVALENTE, 0);
            }
          }
        }
      }
      IMDB.TblMoveNext(IMDBDef3.TBL_CAPUOIMDB2, 0);
      if (IMDB.Eof(IMDBDef3.TBL_CAPUOIMDB2, 0))
      {
        IMDB.TblMoveFirst(IMDBDef3.TBL_CAPUOIMDB2, 0);
      }
      else
      {
        continue Loop1;
      }
      break;
    }
    IMDB.TblInitSort(IMDBDef10.PQRY_CAPUO5_RS);
    IMDB.TblSortBy(IMDBDef10.PQRY_CAPUO5_RS,7,true);
    IMDB.TblSortBy(IMDBDef10.PQRY_CAPUO5_RS,1,true);
    IMDB.TblStartSort(IMDBDef10.PQRY_CAPUO5_RS);
    IMDB.TblMoveFirst(IMDBDef10.PQRY_CAPUO5_RS, 0);
  }

  
  
