  private void PAN_UNITAORGANIZ_InitQueries()
  {
    StringBuffer SQL;

    PAN_UNITAORGANIZ.SetSize(MyGlb.OBJ_QUERY, 3);
    SQL = new StringBuffer();
    SQL.append("select ");
    SQL.append("  A.RESPONSABILE as RESPOUORESPO ");
    SQL.append("from ");
    SQL.append("  RESPONSABILE_UO A ");
    SQL.append("where (A.PROGR_SESSIONE = " + IDL.CSql(MainFrm.PROGRESESSIO, IDL.FMT_NUM, MainFrm.Cf4armDBObject.DBO()) + ") ");
    SQL.append("and   (A.PROGR_UNITA_ORGANIZZATIVA = ~~PROGR_UNITA_ORGANIZZATIVA~~) ");
    PAN_UNITAORGANIZ.SetQuery(PPQRY_RESPONSABIUO, 0, SQL, -1, "");
    PAN_UNITAORGANIZ.SetQueryDB(PPQRY_RESPONSABIUO, MainFrm.Cf4armDBObject.DB, 4);
    PAN_UNITAORGANIZ.SetMasterTable(PPQRY_RESPONSABIUO, "RESPONSABILE_UO");
    SQL = new StringBuffer();
    SQL.append("select ");
    SQL.append("  A.PROGR_UNITA_ORGANIZZATIVA as ANSOANUNOPUO, ");
    SQL.append("  A.COD_SUDDIVISIONE || ' - ' || A.DES_SUDDIVISIONE as GROUPID, ");
    SQL.append("  A.CODICE_UO as ANSOANUNORCU, ");
    SQL.append("  A.CODICE_UO || ' - ' || A.DES_UNITA_ORGANIZZATIVA as ANSOANUNODUO ");
    SQL.append("from ");
    SQL.append("  SO4_ANA_UNITA_ORGANIZZATIVE A ");
    SQL.append("where (A.PROGR_UNITA_ORGANIZZATIVA = ~~PROGR_UNITA_ORGANIZZATIVA~~) ");
    SQL.append("and   (A.OTTICA = " + IDL.CSql(MainFrm.OTTICA, IDL.FMT_CHAR, MainFrm.Cf4armDBObject.DBO()) + ") ");
    SQL.append("and   (A.DAL <= TRUNC( SYSDATE ) AND NVL(A.AL, TRUNC( SYSDATE )) >= TRUNC( SYSDATE )) ");
    SQL.append("order by 2, 3, 4 ");
    PAN_UNITAORGANIZ.SetQuery(PPQRY_SO4ANAUNIORG, 0, SQL, PFL_UNITAORGANIZ_UNITAORGANIZ, "");
    SQL = new StringBuffer();
    SQL.append("select ");
    SQL.append("  A.PROGR_UNITA_ORGANIZZATIVA as ANSOANUNOPUO, ");
    SQL.append("  A.COD_SUDDIVISIONE || ' - ' || A.DES_SUDDIVISIONE as GROUPID, ");
    SQL.append("  A.CODICE_UO as ANSOANUNORCU, ");
    SQL.append("  A.CODICE_UO || ' - ' || A.DES_UNITA_ORGANIZZATIVA as ANSOANUNODUO ");
    SQL.append("from ");
    SQL.append("  SO4_ANA_UNITA_ORGANIZZATIVE A ");
    SQL.append("where (A.OTTICA = " + IDL.CSql(MainFrm.OTTICA, IDL.FMT_CHAR, MainFrm.Cf4armDBObject.DBO()) + ") ");
    SQL.append("and   (A.DAL <= TRUNC( SYSDATE ) AND NVL(A.AL, TRUNC( SYSDATE )) >= TRUNC( SYSDATE )) ");
    SQL.append("order by 2, 3, 4 ");
    PAN_UNITAORGANIZ.SetQuery(PPQRY_SO4ANAUNIORG, 1, SQL, PFL_UNITAORGANIZ_UNITAORGANIZ, "");
    PAN_UNITAORGANIZ.SetQueryDB(PPQRY_SO4ANAUNIORG, MainFrm.Cf4armDBObject.DB, 4);
    PAN_UNITAORGANIZ.SetIMDB(IMDB, "PQRY_CAPUO5", true);
    PAN_UNITAORGANIZ.set_SetString(MyGlb.MASTER_ROWNAME, "Record");
    PAN_UNITAORGANIZ.SetQueryIMDB(PPQRY_CAPUO5, IMDBDef10.PQRY_CAPUO5_RS, IMDBDef3.TBL_CAPUOIMDB2);
    JustLoaded = true;
    PAN_UNITAORGANIZ.SetFieldPrimaryIndex(PFL_UNITAORGANIZ_UNITAORGANIZ, IMDBDef3.FLD_CAPUOIMDB2_PROGR_UNITA_ORGANIZZATIVA);
    PAN_UNITAORGANIZ.SetFieldPrimaryIndex(PFL_UNITAORGANIZ_TIPO, IMDBDef3.FLD_CAPUOIMDB2_TIPO_UNITA_ORGANIZZATIVA);
    PAN_UNITAORGANIZ.SetFieldPrimaryIndex(PFL_UNITAORGANIZ_SCADENZA, IMDBDef3.FLD_CAPUOIMDB2_SCADENZA);
    PAN_UNITAORGANIZ.SetFieldPrimaryIndex(PFL_UNITAORGANIZ_UTENTEINSER1, IMDBDef3.FLD_CAPUOIMDB2_UTENTE_INSERIMENTO);
    PAN_UNITAORGANIZ.SetFieldPrimaryIndex(PFL_UNITAORGANIZ_DATAINSERIM1, IMDBDef3.FLD_CAPUOIMDB2_DATA_INSERIMENTO);
    PAN_UNITAORGANIZ.SetFieldPrimaryIndex(PFL_UNITAORGANIZ_UTENTULTIAG1, IMDBDef3.FLD_CAPUOIMDB2_UTENTE_ULTIMO_AGG);
    PAN_UNITAORGANIZ.SetFieldPrimaryIndex(PFL_UNITAORGANIZ_DATAULTIMAG1, IMDBDef3.FLD_CAPUOIMDB2_DATA_ULTIMO_AGG);
    PAN_UNITAORGANIZ.SetFieldPrimaryIndex(PFL_UNITAORGANIZ_ESERCIZIO, IMDBDef3.FLD_CAPUOIMDB2_ESERCIZIO);
    PAN_UNITAORGANIZ.SetFieldPrimaryIndex(PFL_UNITAORGANIZ_ES, IMDBDef3.FLD_CAPUOIMDB2_E_S);
    PAN_UNITAORGANIZ.SetFieldPrimaryIndex(PFL_UNITAORGANIZ_CAPITOLO, IMDBDef3.FLD_CAPUOIMDB2_CAPITOLO);
    PAN_UNITAORGANIZ.SetFieldPrimaryIndex(PFL_UNITAORGANIZ_ARTICOLO, IMDBDef3.FLD_CAPUOIMDB2_ARTICOLO);
    PAN_UNITAORGANIZ.SetFieldPrimaryIndex(PFL_UNITAORGANIZ_STATO, IMDBDef3.FLD_CAPUOIMDB2_CAPUOIMDBSTA);
    PAN_UNITAORGANIZ.SetFieldPrimaryIndex(PFL_UNITAORGANIZ_PREVALENTE, IMDBDef3.FLD_CAPUOIMDB2_PREVALENTE);
    PAN_UNITAORGANIZ.SetMasterTable(0, "CAPUOIMDB2");
    PAN_UNITAORGANIZ.AddToSortList(PFL_UNITAORGANIZ_DESC1, true);
    PAN_UNITAORGANIZ.AddToSortList(PFL_UNITAORGANIZ_TIPO, true);
    //
    // Rieseguo la query se il pannello era già in stato DATA
    if (PAN_UNITAORGANIZ.Status() == 2)
    {
      int oldListQBE = PAN_UNITAORGANIZ.iUseListQBE;
      PAN_UNITAORGANIZ.iUseListQBE = 0;
      PAN_UNITAORGANIZ.PanelCommand(Glb.PCM_SEARCH);
      PAN_UNITAORGANIZ.PanelCommand(Glb.PCM_FIND);
      PAN_UNITAORGANIZ.iUseListQBE = oldListQBE;
    }
  }

